import React, { useEffect, useState } from "react";
import "./Home.css";

const slides = [
  { id: 1, image: "/images/diwali1.jpg", discount: "60% OFF" },
  { id: 2, image: "/images/diwali2.jpg", discount: "FESTIVAL DEAL" },
  { id: 3, image: "/images/diwali3.jpg", discount: "BUY 1 GET 1" },
];

const Home = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === slides.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="home-container">
      <div className="slideshow">
        <img
          src={slides[currentIndex].image}
          alt={`Slide ${currentIndex}`}
          className="slide-image"
        />
        <div className="discount">{slides[currentIndex].discount}</div>
      </div>

      <div className="main-content">
        {/* Our Companies Section */}
        <div className="companies-section fade-in-on-scroll">
          <h2>Our Companies</h2>
          <div className="company-logos">
            <div className="company-logo-card">
              <img src="/images/company1.png" alt="Company 1" />
              <p>Shree Fireworks</p>
            </div>
            <div className="company-logo-card">
              <img src="/images/company2.png" alt="Company 2" />
              <p>Diwali Delight</p>
            </div>
          </div>
        </div>

        {/* Our Crackers Section */}
        <div className="products-section fade-in-on-scroll">
          <h2>Our Crackers</h2>
          {/* Your product list or cards go here */}
        </div>
      </div>
    </div>
  );
};

export default Home;
