import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ProductList].css";

const ProductList = () => {
  const [allProducts, setAllProducts] = useState([]);
  const [category, setCategory] = useState("All");
  const [searchTerm, setSearchTerm] = useState("");

  const categories = ["All", "Sparklers", "Crackers", "Rockets"];

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((res) => setAllProducts(res.data))
      .catch((err) => console.error("Error fetching products:", err));
  }, []);

  const filteredProducts = allProducts.filter((product) => {
    const inCategory = category === "All" || product.name.toLowerCase().includes(category.toLowerCase());
    const inSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return inCategory && inSearch;
  });

  // Grouped products for 'All' view
  const grouped = categories
    .filter((cat) => cat !== "All")
    .map((cat) => ({
      category: cat,
      items: filteredProducts.filter((p) => p.name.toLowerCase().includes(cat.toLowerCase()))
    }));

  return (
    <div className="product-container">
      <h1>ðŸŽ‡ Cracker Shop</h1>

      <div className="filter-section">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          {categories.map((cat, index) => (
            <option key={index} value={cat}>
              {cat}
            </option>
          ))}
        </select>
        <button onClick={() => setSearchTerm("")}>Clear</button>
      </div>

      {category === "All" ? (
        grouped.map((group) =>
          group.items.length > 0 ? (
            <div key={group.category}>
              <h2>{group.category}</h2>
              <div className="product-grid">
                {group.items.map((product) => (
                  <ProductCard key={product._id} product={product} />
                ))}
              </div>
            </div>
          ) : null
        )
      ) : (
        <div>
          <h2>{category}</h2>
          <div className="product-grid">
            {filteredProducts.length > 0 ? (
              filteredProducts.map((product) => (
                <ProductCard key={product._id} product={product} />
              ))
            ) : (
              <p>No products found.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

const ProductCard = ({ product }) => (
  <div className="product-card">
    <img
      src={`http://localhost:5000${product.image}`}
      alt={product.name}
      className="product-image"
    />
    <h3>{product.name}</h3>
    <p>â‚¹{product.price}</p>
  </div>
);

export default ProductList;
