import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick }) {
  // Load user from localStorage
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  });

  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [showSignin, setShowSignin] = useState(false);

  // Signup state
  const [signupData, setSignupData] = useState({
    name: "",
    phone: "",
    email: "",
    place: "",
    district: "",
  });

  // Signin state
  const [signinPhone, setSigninPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [resendTimer, setResendTimer] = useState(0);

  const dropdownRef = useRef(null);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Countdown for resend OTP button
  useEffect(() => {
    let timer;
    if (resendTimer > 0) {
      timer = setTimeout(() => setResendTimer(resendTimer - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [resendTimer]);

  // ----------------- Signup -----------------
  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${import.meta.env.VITE_API}/api/signup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(signupData),
      });

      const data = await res.json();
      if (data.success) {
        alert("✅ Signup successful! Please sign in.");
        setShowSignup(false);
        setShowSignin(true);
        setSignupData({ name: "", phone: "", email: "", place: "", district: "" });
      } else {
        alert(data.message || "Signup failed.");
      }
    } catch (err) {
      console.error("Signup Error:", err);
      alert("Signup failed. Please try again.");
    }
  };

  // ----------------- Send OTP -----------------
  const handleSendOtp = async () => {
    if (!signinPhone || signinPhone.length < 10) {
      alert("📱 Please enter a valid phone number");
      return;
    }

    try {
      const res = await fetch(`${import.meta.env.VITE_API}/api/signin`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phone: signinPhone }),
      });

      const data = await res.json();
      if (data.success) {
        setOtpSent(true);
        setResendTimer(30);
        if (data.debugOtp) alert(`📲 OTP (Dev Mode): ${data.debugOtp}`);
      } else {
        alert(data.message || "Failed to send OTP.");
      }
    } catch (err) {
      console.error("Signin Error:", err);
      alert("Failed to send OTP. Try again.");
    }
  };

  // ----------------- Verify OTP -----------------
  const handleVerifyOtp = async () => {
    if (!otp || otp.length !== 4) {
      alert("Enter a valid 4-digit OTP");
      return;
    }

    try {
      const res = await fetch(`${import.meta.env.VITE_API}/api/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phone: signinPhone, otp }),
      });

      const data = await res.json();
      if (data.success) {
        localStorage.setItem("user", JSON.stringify(data.user));
        setUser(data.user);
        setSigninPhone("");
        setOtp("");
        setOtpSent(false);
        setShowSignin(false);
        alert("✅ Signed in successfully!");
      } else {
        alert("❌ Invalid OTP");
      }
    } catch (err) {
      console.error("Verify OTP Error:", err);
      alert("Failed to verify OTP. Try again.");
    }
  };

  // ----------------- Logout -----------------
  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    setDropdownOpen(false);
    alert("👋 Logged out successfully.");
  };

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <Link to="/" className="logo">
          Sri Pathrakali Crackers
        </Link>

        <div className="nav-links">
          <Link to="/products">Products</Link>
          <Link to="/about">About</Link>
          <Link to="/after-payment">After Payment</Link>

          {/* Cart */}
          <button className="cart-button" onClick={onCartClick}>
            <ShoppingCart size={22} />
            <span style={{ marginLeft: 4 }}>{cartItemCount}</span>
          </button>

          {/* Auth Section */}
          <div className="auth-section" ref={dropdownRef}>
            {user ? (
              <div style={{ position: "relative" }}>
                <div
                  onClick={() => setDropdownOpen((v) => !v)}
                  className="avatar"
                  title={user.name || user.phone}
                >
                  {user.name ? user.name.charAt(0).toUpperCase() : "?"}
                </div>
                {dropdownOpen && (
                  <div className="dropdown">
                    <div className="dropdown-info">
                      <div className="dropdown-name">{user.name}</div>
                      <div className="dropdown-email">{user.phone}</div>
                      <div className="dropdown-email">{user.email}</div>
                    </div>
                    <hr />
                    <button onClick={handleLogout} className="logout-btn">
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <>
                <button className="auth-btn" onClick={() => setShowSignup(true)}>
                  Sign Up
                </button>
                <button className="auth-btn secondary" onClick={() => setShowSignin(true)}>
                  Sign In
                </button>
              </>
            )}
          </div>
        </div>
      </nav>

      {/* Signup Modal */}
      {showSignup && (
        <div className="modal">
          <div className="modal-content">
            <h2>Sign Up</h2>
            <form onSubmit={handleSignupSubmit}>
              <input type="text" placeholder="Name" required value={signupData.name}
                onChange={(e) => setSignupData({ ...signupData, name: e.target.value })} />
              <input type="tel" placeholder="Phone Number" required value={signupData.phone}
                onChange={(e) => setSignupData({ ...signupData, phone: e.target.value })} />
              <input type="email" placeholder="Email" required value={signupData.email}
                onChange={(e) => setSignupData({ ...signupData, email: e.target.value })} />
              <input type="text" placeholder="Place" required value={signupData.place}
                onChange={(e) => setSignupData({ ...signupData, place: e.target.value })} />
              <input type="text" placeholder="District" required value={signupData.district}
                onChange={(e) => setSignupData({ ...signupData, district: e.target.value })} />
              <button type="submit" className="modal-btn">Submit</button>
            </form>
            <button className="close-btn" onClick={() => setShowSignup(false)}>✖</button>
          </div>
        </div>
      )}

      {/* Signin Modal */}
      {showSignin && (
        <div className="modal">
          <div className="modal-content">
            <h2>Sign In</h2>
            {!otpSent ? (
              <>
                <input type="tel" placeholder="Phone Number" value={signinPhone}
                  onChange={(e) => setSigninPhone(e.target.value)} />
                <button onClick={handleSendOtp} className="modal-btn">Send OTP</button>
              </>
            ) : (
              <>
                <input type="text" placeholder="Enter OTP" value={otp}
                  onChange={(e) => setOtp(e.target.value)} />
                <div style={{ display: "flex", gap: "8px", marginTop: "8px" }}>
                  <button onClick={handleVerifyOtp} className="modal-btn">Verify OTP</button>
                  <button
                    onClick={() => { setOtpSent(false); setOtp(""); }}
                    className="modal-btn secondary"
                    style={{ backgroundColor: "#ddd", color: "#333" }}
                  >
                    Change Number
                  </button>
                </div>
                <button
                  disabled={resendTimer > 0}
                  onClick={handleSendOtp}
                  className="modal-btn secondary"
                  style={{ marginTop: "10px" }}
                >
                  {resendTimer > 0 ? `Resend in ${resendTimer}s` : "Resend OTP"}
                </button>
              </>
            )}
            <button className="close-btn" onClick={() => setShowSignin(false)}>✖</button>
          </div>
        </div>
      )}
    </>
  );
}

export default Navbar;
