// Products.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem("cart");
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((res) => {
        const prods = res.data.map((p) => ({
          ...p,
          category: p.category ? p.category.trim().toLowerCase() : "",
        }));
        setProducts(prods);
      })
      .catch((err) => {
        console.error("Error loading products:", err);
      });

    axios.get("http://localhost:5000/api/categories")
      .then((res) => {
        const cats = res.data.map((c) => c.trim().toLowerCase());
        setCategories(cats);
      })
      .catch((err) => {
        console.error("Error loading categories:", err);
      });
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter((p) => p.category === selectedCategory)
    : products;

  const capitalize = (str) =>
    str && str.length > 0 ? str.charAt(0).toUpperCase() + str.slice(1) : "";

  const addToCart = (product) => {
    setCart((prev) => {
      const existing = prev.find((item) => item.productId === product._id);
      if (existing) {
        return prev.map((item) =>
          item.productId === product._id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prev, { productId: product._id, quantity: 1, productData: product }];
      }
    });
  };

  const updateQuantity = (productId, delta) => {
    setCart((prev) => {
      return prev
        .map((item) =>
          item.productId === productId
            ? { ...item, quantity: Math.max(1, item.quantity + delta) }
            : item
        )
        .filter((item) => item.quantity > 0);
    });
  };

  const removeFromCart = (productId) => {
    setCart((prev) => prev.filter((item) => item.productId !== productId));
  };

  const totalPrice = cart.reduce(
    (acc, item) => acc + item.productData.price * item.quantity,
    0
  );

  return (
    <div className="products-container">
      <div className="sidebar">
        <div className="sidebar-header">
          <strong>Categories</strong>
        </div>
        <ul>
          {categories.map((cat, index) => (
            <li
              key={index}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {capitalize(cat)}{" "}
              <span className="count">
                ({products.filter((p) => p.category === cat).length})
              </span>
            </li>
          ))}
        </ul>
        <button
          className="clear-btn"
          onClick={() => setSelectedCategory(null)}
          disabled={selectedCategory === null}
        >
          Show All
        </button>
      </div>

      <div className="products-section">
        <div className="products-header">
          <h2>{selectedCategory ? capitalize(selectedCategory) : "All Products"}</h2>
          <span>Showing {filteredProducts.length} results</span>
        </div>

        <div className="product-grid">
          {filteredProducts.map((p) => (
            <div className="product-card" key={p._id}>
              <div className="image-wrapper">
                <img
                  src={
                    p.image && p.image.startsWith("http")
                      ? p.image
                      : "https://via.placeholder.com/200x150?text=Image+Coming+Soon"
                  }
                  alt={p.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://via.placeholder.com/200x150?text=Image+Coming+Soon";
                  }}
                />
                <div className="offer-badge">80% OFF</div>
              </div>
              <p className="category-label">{capitalize(p.category)}</p>
              <h3>{p.name}</h3>
              <p className="price">₹{p.price}</p>
              <button className="enquiry-btn" onClick={() => addToCart(p)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>

      <div
        className="sidebar"
        style={{ maxWidth: "350px", marginLeft: "20px", height: "fit-content" }}
      >
        <div className="sidebar-header">
          <strong>Your Cart</strong> ({cart.length} items)
        </div>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cart.map((item) => (
            <div
              key={item.productId}
              style={{
                display: "flex",
                alignItems: "center",
                marginBottom: "15px",
                gap: "10px",
                borderBottom: "1px solid #eee",
                paddingBottom: "10px",
              }}
            >
              <img
                src={item.productData.image}
                alt={item.productData.name}
                width={60}
                height={50}
                style={{ objectFit: "contain", borderRadius: "6px" }}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://via.placeholder.com/60x50?text=No+Image";
                }}
              />
              <div style={{ flex: 1 }}>
                <strong>{item.productData.name}</strong>
                <div>
                  <button
                    onClick={() => updateQuantity(item.productId, -1)}
                    style={{ marginRight: "6px" }}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.productId, +1)}
                    style={{ marginLeft: "6px" }}
                  >
                    +
                  </button>
                  <button
                    onClick={() => removeFromCart(item.productId)}
                    style={{ marginLeft: "10px", color: "red", cursor: "pointer" }}
                    title="Remove"
                  >
                    ✕
                  </button>
                </div>
              </div>
              <div style={{ fontWeight: "bold" }}>
                ₹{(item.productData.price * item.quantity).toFixed(2)}
              </div>
            </div>
          ))
        )}
        {cart.length > 0 && (
          <div
            style={{
              marginTop: "20px",
              fontWeight: "700",
              fontSize: "1.2rem",
              borderTop: "1px solid #ddd",
              paddingTop: "10px",
            }}
          >
            Total: ₹{totalPrice.toFixed(2)}
          </div>
        )}
      </div>
    </div>
  );
}
