import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [selectedType, setSelectedType] = useState('All');
  const [cart, setCart] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => {
        const fixedProducts = res.data.map((product, index) => ({
          ...product,
          _id: product._id || `fallback-${index}`, // ✅ Ensure unique fallback ID
          image: product.image?.startsWith('http')
            ? product.image
            : `http://localhost:5000/images/cracker${(index % 10) + 1}.jpg`, // ✅ Fallback image
        }));
        setProducts(fixedProducts);
      })
      .catch(err => console.error('Failed to fetch products:', err));
  }, []);

  const getProductTypes = () => {
    const types = [...new Set(products.map(p => p.category || p.type))];
    return ['All', ...types];
  };

  const filtered = selectedType === 'All'
    ? products
    : products.filter(p => (p.category || p.type) === selectedType);

  const handleQuantity = (id, delta) => {
    setCart(prev => {
      const qty = (prev[id] || 0) + delta;
      return { ...prev, [id]: Math.max(qty, 0) };
    });
  };

  const handleAddToCart = (id) => {
    setCart(prev => ({ ...prev, [id]: (prev[id] || 1) }));
    alert('Added to cart!');
  };

  return (
    <div className="product-page">
      <aside className="sidebar">
        {getProductTypes().map((type) => (
          <button
            key={type} // ✅ Safe: type is unique
            className={selectedType === type ? 'active' : ''}
            onClick={() => setSelectedType(type)}
          >
            {type}
          </button>
        ))}
      </aside>

      <main className="product-grid">
        {filtered.map((product, index) => (
          <div key={product._id || index} className="product-card"> {/* ✅ Fallback key using index if needed */}
            <img src={product.image} alt={product.name} />
            <h4>{product.name}</h4>
            <p>₹{product.price}</p>
            <div className="quantity">
              <button onClick={() => handleQuantity(product._id, -1)}>-</button>
              <span>{cart[product._id] || 0}</span>
              <button onClick={() => handleQuantity(product._id, 1)}>+</button>
            </div>
            <button className="add-btn" onClick={() => handleAddToCart(product._id)}>
              Add to Cart
            </button>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Products;
