import React from "react";
import "./Home.css";

// Import images
import diwali1 from "./publi/diwali1.jpg";  // adjust path as needed

export default function Home() {
  // You can create an array of images for the slideshow
  const images = [diwali1, diwali1, diwali1, diwali1, diwali1];

  return (
    <div className="home-container">
      <div className="greeting">
        <h1 className="animated-text">Welcome to CrackerMart</h1>
      </div>

      <div className="slideshow">
        <div className="slide-track">
          {images.map((imgSrc, index) => (
            <img key={index} src={imgSrc} alt={`Firework ${index + 1}`} className="slide" />
          ))}
        </div>
      </div>
    </div>
  );
}
