// cartdrawer.jsx
import React, { useState } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';

function CartDrawer({
  isOpen,
  onClose,
  cartItems,
  onRemoveFromCart,
  onUpdateQuantity
}) {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    location: ''
  });

  const handleInputChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFirstSubmit = () => {
    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    if (total < 3000) {
      alert('⚠️ Purchase minimum ₹3000 to proceed.');
      return;
    }
    if (cartItems.length === 0) {
      alert('⚠️ Your cart is empty.');
      return;
    }
    setShowForm(true);
  };

  const handleFormSubmit = () => {
    const { name, phone, location } = formData;

    if (!name.trim() || !phone.trim() || !location.trim()) {
      alert('⚠️ Please fill in all the details before submitting.');
      return;
    }

    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

    const message = `🛍️ *New Order from CrackerMart* 🧨

👤 *Customer Details*:
• Name: ${name}
• Phone: ${phone}
• Location: ${location}

📦 *Items Ordered*:
${cartItems.map(item =>
  `• ${item.name} x${item.quantity} = ₹${item.price * item.quantity}`
).join('\n')}

💰 *Total Amount*: ₹${total.toFixed(2)}

✅ Kindly confirm the order and provide delivery details.
`;

    const encodedMessage = encodeURIComponent(message);
    const whatsappNumber = '8148842731';
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodedMessage}`;

    window.open(whatsappURL, '_blank');
  };

  const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0);

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">×</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
                disabled={false}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>Total: ₹{total.toFixed(2)}</h3>

            {!showForm && (
              <button className="purchase-button" onClick={handleFirstSubmit}>
                Submit
              </button>
            )}

            {showForm && (
              <div className="checkout-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="location"
                  placeholder="Your Place"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                />

                <button className="submit-final" onClick={handleFormSubmit}>
                  📤 Send Bill & Order to WhatsApp
                </button>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
