// Register.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleRegister = (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      alert("‚ùå Passwords do not match!");
      return;
    }

    // Here you could call your backend API to save the user
    alert(`‚úÖ Registered with Email: ${email}`);
    alert("üéâ You are now logged in!");

    // Redirect to home or login page if needed
    // navigate("/login"); // if using useNavigate from react-router-dom
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "400px", margin: "auto" }}>
      <h2>Create Account</h2>
      <form
        onSubmit={handleRegister}
        style={{ display: "flex", flexDirection: "column" }}
      >
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ margin: "0.5rem 0", padding: "0.5rem" }}
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ margin: "0.5rem 0", padding: "0.5rem" }}
        />

        <input
          type="password"
          placeholder="Confirm Password"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          style={{ margin: "0.5rem 0", padding: "0.5rem" }}
        />

        <button
          type="submit"
          style={{
            padding: "0.7rem",
            background: "#dd2476",
            color: "white",
            border: "none",
            marginTop: "0.5rem",
          }}
        >
          Register & Login
        </button>
      </form>

      <p style={{ marginTop: "1rem", textAlign: "center" }}>
        Already have an account?{" "}
        <Link to="/login" style={{ color: "#dd2476", fontWeight: "bold" }}>
          Login
        </Link>
      </p>
    </div>
  );
}
