// Products.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ProductItem from '../components/ProductItem';
import './Products.css';

function Products({ onAddToCart }) {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('All');
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(res => res.json())
      .then(data => {
        const formatted = data.map(p => ({
          ...p,
          imageUrl: `http://localhost:5000${p.image}`,
        }));
        setProducts(formatted);
        setFiltered(formatted);

        // Extract unique categories from products
        const cats = ['All', ...new Set(formatted.map(p => p.category || 'Others'))];
        setCategories(cats);
      })
      .catch(err => {
        console.error("Failed to fetch products:", err);
      });
  }, []);

  // Filter products when search/category changes
  useEffect(() => {
    let temp = [...products];

    if (category !== 'All') {
      temp = temp.filter(p => p.category === category);
    }

    if (search.trim()) {
      const keyword = search.toLowerCase();
      temp = temp.filter(p => p.name.toLowerCase().includes(keyword));
    }

    setFiltered(temp);
  }, [search, category, products]);

  return (
    <div className="products-page">
      <h2>Our Products</h2>

      <div className="filters">
        <input
          type="text"
          placeholder="üîç Search products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          {categories.map((cat, i) => (
            <option key={i} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <div className="products-grid">
        {filtered.length === 0 ? (
          <p>‚ùå No products found.</p>
        ) : (
          filtered.map((product) => (
            <ProductItem
              key={product._id}
              product={product}
              onAddToCart={() => onAddToCart(product, navigate)}
            />
          ))
        )}
      </div>
    </div>
  );
}

export default Products;
