import React, { useState } from 'react';
import AfterPayment from './AddProduct';
import './AfterPayment.css';
 
function SubmitOrder({ cartItems }) {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [place, setPlace] = useState('');
  const [landmark, setLandmark] = useState('');
  const [billFile, setBillFile] = useState(null);
  const [screenshotFile, setScreenshotFile] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!billFile || !screenshotFile) {
      alert("Please upload both bill and screenshot.");
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('phone', phone);
    formData.append('place', place);
    formData.append('landmark', landmark);
    formData.append('bill', billFile);
    formData.append('screenshot', screenshotFile);

    try {
      const res = await fetch('http://localhost:5000/api/order-upload', {
        method: 'POST',
        body: formData
      });

      const data = await res.json();

      if (data.success) {
        window.location.href = data.whatsappURL;
      } else {
        alert('Upload failed. Try again.');
      }
    } catch (err) {
      console.error(err);
      alert('Error submitting order.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '12px', maxWidth: '400px', margin: 'auto' }}>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
      <input type="text" placeholder="Phone Number" value={phone} onChange={(e) => setPhone(e.target.value)} required />
      <input type="text" placeholder="Place" value={place} onChange={(e) => setPlace(e.target.value)} required />
      <input type="text" placeholder="Landmark" value={landmark} onChange={(e) => setLandmark(e.target.value)} required />

      <label>Upload PDF Bill</label>
      <input type="file" accept="application/pdf" onChange={(e) => setBillFile(e.target.files[0])} required />

      <label>Upload Payment Screenshot</label>
      <input type="file" accept="image/*" onChange={(e) => setScreenshotFile(e.target.files[0])} required />

      <button type="submit" style={{ backgroundColor: 'green', color: 'white', padding: '10px', borderRadius: '6px', border: 'none' }}>
        Submit Order to WhatsApp
      </button>
    </form>
  );
}

export default AfterPayme;
