// src/components/Navbar.jsx
import React from "react";
import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";
import { GoogleLogin } from "@react-oauth/google"; // ✅ Correct import
import {jwtDecode} from "jwt-decode"; // ✅ Correct way to import jwt-decode
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick }) {
  const user = JSON.parse(localStorage.getItem("user"));

  const handleSuccess = (credentialResponse) => {
    try {
      const userData = jwtDecode(credentialResponse.credential); // ✅ Decode token
      localStorage.setItem("user", JSON.stringify(userData));
      window.location.reload(); // refresh to show user in navbar
    } catch (err) {
      console.error("JWT Decode Error:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  return (
    <nav className="navbar">
      {/* Logo */}
      <Link to="/" className="logo">
        Sri Pathrakali Crackers
      </Link>

      {/* Navigation Links */}
      <div className="nav-links">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/after-payment">After Payment</Link>

        {/* Cart */}
        <button className="cart-button" onClick={onCartClick}>
          <ShoppingCart size={22} />
          <span style={{ marginLeft: "4px" }}>{cartItemCount}</span>
        </button>

        {/* Auth Section */}
        <div className="auth-section">
          {user ? (
            // ✅ If logged in → show profile pic + logout
            <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
              <img
                src={user.picture}
                alt="profile"
                style={{ width: "32px", height: "32px", borderRadius: "50%" }}
              />
              <button
                onClick={handleLogout}
                style={{
                  background: "transparent",
                  border: "none",
                  color: "red",
                  cursor: "pointer",
                }}
              >
                Logout
              </button>
            </div>
          ) : (
            // ❌ If not logged in → show Google Sign-In button
            <GoogleLogin
              onSuccess={handleSuccess}
              onError={() => alert("❌ Login Failed")}
              useOneTap
            />
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
