import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState({});

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => {
        console.log("Fetched products:", res.data);
        setProducts(res.data);

        const uniqueCategories = [
          ...new Set(
            res.data.map((p) => p.category?.trim()).filter(Boolean)
          )
        ].sort();
        setCategories(uniqueCategories);
      })
      .catch((err) => {
        console.error("Error fetching products:", err.message);
        setCategories(["Sparklers", "Flower Pots", "Crackers"]); // fallback
      });
  }, []);

  const filteredProducts =
    selectedCategory === null
      ? products
      : products.filter(
          (p) => p.category?.trim() === selectedCategory.trim()
        );

  const addToCart = (product) => {
    setCart((prev) => ({
      ...prev,
      [product._id]: {
        ...product,
        quantity: prev[product._id] ? prev[product._id].quantity + 1 : 1
      }
    }));
  };

  const removeFromCart = (id) => {
    setCart((prev) => {
      const updated = { ...prev };
      if (updated[id]) {
        if (updated[id].quantity > 1) updated[id].quantity--;
        else delete updated[id];
      }
      return updated;
    });
  };

  const total = Object.values(cart).reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="product-page">
      <aside className="sidebar">
        <h2>Product Categories</h2>
        <ul>
          <li
            className={!selectedCategory ? "active" : ""}
            onClick={() => setSelectedCategory(null)}
          >
            All
          </li>
          {categories.map((cat) => (
            <li
              key={cat}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      <main className="main-content">
        <div className="product-grid">
          {filteredProducts.length === 0 ? (
            <p className="no-products-message">
              {products.length === 0
                ? "Loading or server down..."
                : "No products in this category"}
            </p>
          ) : (
            filteredProducts.map((p) => (
              <div key={p._id} className="product-card">
                <img src={p.image} alt={p.name} />
                <h3>{p.name}</h3>
                <p>₹{p.price}</p>
                <div className="quantity-control">
                  <button onClick={() => removeFromCart(p._id)}>-</button>
                  <span>{cart[p._id]?.quantity || 0}</span>
                  <button onClick={() => addToCart(p)}>+</button>
                </div>
                <button className="add-btn" onClick={() => addToCart(p)}>
                  Add to Cart
                </button>
              </div>
            ))
          )}
        </div>

        {Object.keys(cart).length > 0 && (
          <div className="cart-box">
            <h3>Cart</h3>
            <ul>
              {Object.values(cart).map((item) => (
                <li key={item._id}>
                  {item.name} x {item.quantity} = ₹
                  {item.price * item.quantity}
                </li>
              ))}
            </ul>
            <h4>Total: ₹{total}</h4>
            <button className="pay-btn">Pay Now</button>
          </div>
        )}
      </main>
    </div>
  );
}
