// src/components/Navbar.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";
import "./Navbar.css";

const API_BASE = process.env.REACT_APP_API || ""; // empty => relative paths like /api/...

function Navbar({ cartItemCount, onCartClick }) {
  const [user, setUser] = useState(() => {
    try { return JSON.parse(localStorage.getItem("user")) || null; }
    catch { return null; }
  });

  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [showSignin, setShowSignin] = useState(false);

  const [signupData, setSignupData] = useState({
    name: "", phone: "", email: "", place: "", district: ""
  });

  const [signinPhone, setSigninPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [resendTimer, setResendTimer] = useState(0);

  const dropdownRef = useRef(null);

  // debug
  useEffect(() => {
    console.log("API_BASE =", API_BASE);
  }, []);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    let t;
    if (resendTimer > 0) t = setTimeout(() => setResendTimer((s) => s - 1), 1000);
    return () => clearTimeout(t);
  }, [resendTimer]);

  // Signup
  const handleSignupSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_BASE}/api/signup`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(signupData),
      });
      const data = await res.json();
      if (data.success) {
        alert("âœ… Signup successful! Please sign in.");
        setShowSignup(false);
        setShowSignin(true);
        setSignupData({ name: "", phone: "", email: "", place: "", district: "" });
      } else {
        alert(data.message || "Signup failed.");
      }
    } catch (err) {
      console.error("Signup Error:", err);
      alert("Signup failed. Check console.");
    }
  };

  // Send OTP
  const handleSendOtp = async () => {
    if (!signinPhone || signinPhone.trim().length < 6) { // basic validation
      return alert("Please enter a valid phone number (with country code if required).");
    }
    try {
      const res = await fetch(`${API_BASE}/api/signin`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phone: signinPhone }),
      });
      const data = await res.json();
      if (data.success) {
        setOtpSent(true);
        setResendTimer(30);
        if (data.debugOtp) {
          // show OTP only in development/debug
          alert(`ðŸ“² Dev OTP: ${data.debugOtp}`);
        } else {
          alert("ðŸ“² OTP sent to your phone (if SMS configured).");
        }
      } else {
        alert(data.message || "Failed to send OTP.");
      }
    } catch (err) {
      console.error("Signin Error:", err);
      alert("Failed to contact server. Check API URL and server status.");
    }
  };

  // Verify OTP
  const handleVerifyOtp = async () => {
    if (!otp || otp.trim().length < 3) return alert("Enter a valid OTP.");
    try {
      const res = await fetch(`${API_BASE}/api/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phone: signinPhone, otp }),
      });
      const data = await res.json();
      if (data.success) {
        localStorage.setItem("user", JSON.stringify(data.user));
        setUser(data.user);
        setSigninPhone("");
        setOtp("");
        setOtpSent(false);
        setShowSignin(false);
        alert("âœ… Signed in successfully!");
      } else {
        alert(data.message || "Invalid OTP");
      }
    } catch (err) {
      console.error("Verify OTP Error:", err);
      alert("Failed to verify OTP. Try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    setDropdownOpen(false);
    alert("Logged out.");
  };

  return (
    <>
      <nav className="navbar">
        <Link to="/" className="logo">Sri Pathrakali Crackers</Link>
        <div className="nav-links">
          <Link to="/products">Products</Link>
          <Link to="/about">About</Link>
          <Link to="/after-payment">After Payment</Link>

          <button className="cart-button" onClick={onCartClick}>
            <ShoppingCart size={22} />
            <span style={{ marginLeft: 6 }}>{cartItemCount}</span>
          </button>

          <div className="auth-section" ref={dropdownRef}>
            {user ? (
              <div style={{ position: "relative" }}>
                <div onClick={() => setDropdownOpen((v) => !v)} className="avatar" title={user.name || user.phone}>
                  {user.name ? user.name.charAt(0).toUpperCase() : "?"}
                </div>
                {dropdownOpen && (
                  <div className="dropdown">
                    <div className="dropdown-info">
                      <div className="dropdown-name">{user.name}</div>
                      <div className="dropdown-email">{user.phone}</div>
                      <div className="dropdown-email">{user.email}</div>
                    </div>
                    <hr />
                    <button onClick={handleLogout} className="logout-btn">Logout</button>
                  </div>
                )}
              </div>
            ) : (
              <>
                <button className="auth-btn" onClick={() => setShowSignup(true)}>Sign Up</button>
                <button className="auth-btn secondary" onClick={() => setShowSignin(true)}>Sign In</button>
              </>
            )}
          </div>
        </div>
      </nav>

      {showSignup && (
        <div className="modal">
          <div className="modal-content">
            <h2>Sign Up</h2>
            <form onSubmit={handleSignupSubmit}>
              <input required placeholder="Name" value={signupData.name} onChange={(e)=>setSignupData({...signupData, name:e.target.value})} />
              <input required placeholder="Phone" value={signupData.phone} onChange={(e)=>setSignupData({...signupData, phone:e.target.value})} />
              <input required placeholder="Email" type="email" value={signupData.email} onChange={(e)=>setSignupData({...signupData, email:e.target.value})} />
              <input required placeholder="Place" value={signupData.place} onChange={(e)=>setSignupData({...signupData, place:e.target.value})} />
              <input required placeholder="District" value={signupData.district} onChange={(e)=>setSignupData({...signupData, district:e.target.value})} />
              <button type="submit" className="modal-btn">Submit</button>
            </form>
            <button className="close-btn" onClick={()=>setShowSignup(false)}>âœ–</button>
          </div>
        </div>
      )}

      {showSignin && (
        <div className="modal">
          <div className="modal-content">
            <h2>Sign In</h2>
            {!otpSent ? (
              <>
                <input placeholder="Phone" value={signinPhone} onChange={(e)=>setSigninPhone(e.target.value)} />
                <button onClick={handleSendOtp} className="modal-btn">Send OTP</button>
              </>
            ) : (
              <>
                <input placeholder="Enter OTP" value={otp} onChange={(e)=>setOtp(e.target.value)} />
                <div style={{display:"flex",gap:8,marginTop:8}}>
                  <button onClick={handleVerifyOtp} className="modal-btn">Verify OTP</button>
                  <button onClick={()=>{ setOtpSent(false); setOtp(""); }} className="modal-btn secondary">Change Number</button>
                </div>
                <button disabled={resendTimer>0} onClick={handleSendOtp} className="modal-btn secondary" style={{marginTop:10}}>
                  {resendTimer>0 ? `Resend in ${resendTimer}s` : "Resend OTP"}
                </button>
              </>
            )}
            <button className="close-btn" onClick={()=>setShowSignin(false)}>âœ–</button>
          </div>
        </div>
      )}
    </>
  );
}

export default Navbar;
