import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [selectedType, setSelectedType] = useState('All');
  const [cart, setCart] = useState({});

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => {
        const fixedProducts = res.data.map((product, index) => ({
          ...product,
          _id: product._id || `fallback-${index}`, // âœ… Fallback _id
          image: product.image?.startsWith('http')
            ? product.image
            : `http://localhost:5000/images/cracker${(index % 10) + 1}.jpg`, // âœ… Fallback image
          type: (product.category || product.type || 'Unknown').toLowerCase(), // âœ… Normalize type
        }));
        setProducts(fixedProducts);
        console.log('Fetched products:', fixedProducts); // ðŸ” Debug: see what types exist
      })
      .catch(err => console.error('Failed to fetch products:', err));
  }, []);

  const getProductTypes = () => {
    const types = [...new Set(products.map(p => p.type))];
    return ['All', ...types.map(t => t.charAt(0).toUpperCase() + t.slice(1))]; // âœ… Capitalize
  };

  const filtered = selectedType === 'All'
    ? products
    : products.filter(p =>
        p.type.toLowerCase() === selectedType.toLowerCase()
      );

  const handleQuantity = (id, delta) => {
    setCart(prev => {
      const qty = (prev[id] || 0) + delta;
      return { ...prev, [id]: Math.max(qty, 0) };
    });
  };

  const handleAddToCart = (id) => {
    setCart(prev => ({ ...prev, [id]: (prev[id] || 1) }));
    alert('Added to cart!');
  };

  return (
    <div className="product-page">
      <aside className="sidebar">
        {getProductTypes().map((type) => (
          <button
            key={type}
            className={selectedType.toLowerCase() === type.toLowerCase() ? 'active' : ''}
            onClick={() => setSelectedType(type)}
          >
            {type}
          </button>
        ))}
      </aside>

      <main className="product-grid">
        {filtered.length === 0 ? (
          <p style={{ textAlign: 'center', width: '100%' }}>No products available.</p>
        ) : (
          filtered.map((product, index) => (
            <div key={product._id || index} className="product-card">
              <img src={product.image} alt={product.name} />
              <h4>{product.name}</h4>
              <p>â‚¹{product.price}</p>
              <div className="quantity">
                <button onClick={() => handleQuantity(product._id, -1)}>-</button>
                <span>{cart[product._id] || 0}</span>
                <button onClick={() => handleQuantity(product._id, 1)}>+</button>
              </div>
              <button className="add-btn" onClick={() => handleAddToCart(product._id)}>
                Add to Cart
              </button>
            </div>
          ))
        )}
      </main>
    </div>
  );
};

export default Products;
