// src/components/CartDrawer.jsx
import React from "react";
import { jsPDF } from "jspdf";

const CartDrawer = ({ isOpen, onClose, cartItems }) => {
  if (!isOpen) return null;

  // Calculate total based on discounted subtotals
  const discountPercent = 80;
  const getDiscountedPrice = (price) =>
    Math.round(price * (100 - discountPercent) / 100);

  const total = cartItems.reduce((acc, item) => {
    const subtotal = getDiscountedPrice(item.price) * item.quantity;
    return acc + subtotal;
  }, 0);

  const handleDownloadBill = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text("ðŸ§¨ CrackerMart Bill", 14, 20);
    doc.setFontSize(12);

    let y = 40;
    doc.text("No.", 14, y);
    doc.text("Item", 30, y);
    doc.text("Qty", 100, y);
    doc.text("Price", 130, y);
    doc.text("Subtotal", 170, y);

    y += 10;

    cartItems.forEach((item, index) => {
      const originalPrice = item.price;
      const discountedPrice = getDiscountedPrice(originalPrice);
      const subtotal = discountedPrice * item.quantity;

      doc.text(String(index + 1), 14, y);
      doc.text(item.name, 30, y);
      doc.text(String(item.quantity), 100, y);

      // Price: show discounted (original in brackets)
      doc.text(
        `â‚¹${discountedPrice} (â‚¹${originalPrice})`,
        130,
        y,
        { align: "right" }
      );

      doc.text(`â‚¹${subtotal}`, 190, y, { align: "right" });
      y += 10;
    });

    // Total
    y += 10;
    doc.setFontSize(14);
    doc.text(`Total: â‚¹${total}`, 150, y, { align: "right" });

    doc.save("CrackerMart-Bill.pdf");
  };

  return (
    <div className="fixed top-0 right-0 w-96 h-full bg-white shadow-lg p-4 overflow-y-auto z-50">
      <button
        onClick={onClose}
        className="text-red-500 font-bold text-lg float-right"
      >
        âœ–
      </button>
      <h2 className="text-xl font-bold mb-4">ðŸ›’ Your Cart</h2>

      {cartItems.length === 0 ? (
        <p className="text-gray-500">No items in cart</p>
      ) : (
        <div>
          <table className="w-full border-collapse">
            <thead>
              <tr className="border-b">
                <th className="p-2 text-left">No.</th>
                <th className="p-2 text-left">Item</th>
                <th className="p-2 text-center">Qty</th>
                <th className="p-2 text-right">Price</th>
                <th className="p-2 text-right">Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item, index) => {
                const originalPrice = item.price;
                const discountedPrice = getDiscountedPrice(originalPrice);
                const subtotal = discountedPrice * item.quantity;

                return (
                  <tr key={index} className="border-b">
                    <td className="p-2 text-center">{index + 1}</td>
                    <td className="p-2">{item.name}</td>
                    <td className="p-2 text-center">{item.quantity}</td>
                    <td className="p-2 text-right">
                      <span className="line-through text-red-500 mr-1">
                        â‚¹{originalPrice}
                      </span>
                      <span className="text-green-600 font-bold">
                        â‚¹{discountedPrice}
                      </span>
                    </td>
                    <td className="p-2 text-right font-bold">â‚¹{subtotal}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>

          <div className="text-right mt-4 font-bold text-lg">
            Total: â‚¹{total}
          </div>

          <button
            onClick={handleDownloadBill}
            className="mt-4 bg-green-600 text-white px-4 py-2 rounded-lg shadow hover:bg-green-700"
          >
            â¬‡ Download Bill
          </button>
        </div>
      )}
    </div>
  );
};

export default CartDrawer;
