import React, { useState } from 'react';
import './CartDrawer.css';

function CartDrawer({ isOpen, onClose, cartItems, onRemoveFromCart, onUpdateQuantity }) {
  const [showForm, setShowForm] = useState(false);
  const [hasSubmittedOnce, setHasSubmittedOnce] = useState(false); // Track form display status
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    location: '',
  });

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleInputChange = (e) => {
    setFormData((prevData) => ({
      ...prevData,
      [e.target.name]: e.target.value,
    }));
  };

  const handleFormSubmit = async () => {
    const orderData = {
      customerName: formData.name,
      phone: formData.phone,
      address: formData.location,
      orderItems: cartItems,
      total: calculateTotal(),
    };

    const whatsappMessage = `*Order Details*:\n\n${cartItems
      .map((item) => `• ${item.name} x ${item.quantity} = ₹${item.price * item.quantity}`)
      .join('\n')}\n\n*Total*: ₹${calculateTotal()}\n\n*Customer Details:*\nName: ${
      formData.name
    }\nPhone: ${formData.phone}\nLocation: ${formData.location}`;

    const whatsappURL = `https://wa.me/<YOUR_PHONE_NUMBER>?text=${encodeURIComponent(whatsappMessage)}`;
    window.open(whatsappURL, '_blank');
  };

  const handleFirstSubmit = () => {
    const total = calculateTotal();
    if (total < 3000) {
      alert('⚠️ Minimum order value is ₹3000 to proceed.');
      return;
    }
    setShowForm(true);
    setHasSubmittedOnce(true); // Remember that form was shown
  };

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <button className="close-button" onClick={onClose}>
        ✕
      </button>
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item) => (
            <div className="cart-item" key={item._id}>
              <img src={item.image} alt={item.name} />
              <div className="item-details">
                <h4>{item.name}</h4>
                <p>₹{item.price}</p>
                <div className="quantity-controls">
                  <button onClick={() => onUpdateQuantity(item._id, item.quantity - 1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => onUpdateQuantity(item._id, item.quantity + 1)}>+</button>
                </div>
                <button onClick={() => onRemoveFromCart(item._id)}>Remove</button>
              </div>
            </div>
          ))}

          <div className="cart-total">Total: ₹{calculateTotal()}</div>

          {!showForm && !hasSubmittedOnce && (
            <button className="purchase-button" onClick={handleFirstSubmit}>
              Submit
            </button>
          )}

          {showForm && (
            <div className="checkout-form">
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <input
                type="tel"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="location"
                placeholder="Your Place"
                value={formData.location}
                onChange={handleInputChange}
                required
              />

              <button className="submit-final" onClick={handleFormSubmit}>
                Provide the Details with Bill
              </button>

              <button
                className="back-button"
                onClick={() => setShowForm(false)}
                style={{ marginTop: '10px', backgroundColor: '#ddd' }}
              >
                ← Back to Cart
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default CartDrawer;
