import React, { useState } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import './CartDrawer.css'; // if you have existing CSS

const CartDrawer = ({ cartItems, onClose, onClearCart }) => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '', location: '' });

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  const handleFormSubmit = async () => {
    const { name, phone, location } = formData;

    if (!name.trim() || !phone.trim() || !location.trim()) {
      alert('‚ö†Ô∏è Please fill in all the details before submitting.');
      return;
    }

    const now = new Date();
    const date = now.toLocaleDateString('en-IN');
    const time = now.toLocaleTimeString('en-IN');
    const total = calculateTotal();

    // Create printable bill content
    const billElement = document.createElement('div');
    billElement.style.padding = '20px';
    billElement.style.fontFamily = 'Arial';
    billElement.style.width = '600px';
    billElement.innerHTML = `
      <h2>üß® CrackerMart Order Bill üß®</h2>
      <p><b>Order Time:</b> ${date} - ${time}</p>
      <h3>üë§ Customer Info</h3>
      <p>Name: ${name}</p>
      <p>Phone: ${phone}</p>
      <p>Location: ${location}</p>
      <h3>üì¶ Items Ordered</h3>
      <ul>
        ${cartItems.map((item, i) => `
          <li>${i + 1}. ${item.name} - Qty: ${item.quantity} √ó ‚Çπ${item.price} = ‚Çπ${item.price * item.quantity}</li>
        `).join('')}
      </ul>
      <h3>üí∞ Total Bill: ‚Çπ${total.toFixed(2)}</h3>
      <p>üìå Thank you for your order!</p>
    `;
    document.body.appendChild(billElement);

    // Generate PDF from HTML content
    const canvas = await html2canvas(billElement);
    const imgData = canvas.toDataURL('image/png');

    const pdf = new jsPDF();
    const imgProps = pdf.getImageProperties(imgData);
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
    pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);

    const pdfBlob = pdf.output('blob');

    // Download PDF to local system
    pdf.save('CrackerMart_Bill.pdf');

    // Generate WhatsApp message with bill link
    const file = new File([pdfBlob], 'CrackerMart_Bill.pdf', { type: 'application/pdf' });
    const fileURL = URL.createObjectURL(file);
    const whatsappMessage = `Hi, I‚Äôve placed my order. Here's the bill: ${fileURL}`;
    const whatsappNumber = '8148842731'; // replace with your number
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(whatsappMessage)}`;
    window.open(whatsappURL, '_blank');

    // Clean up
    document.body.removeChild(billElement);
    if (onClearCart) onClearCart();
    onClose();
    setShowForm(false);
    setFormData({ name: '', phone: '', location: '' });
  };

  return (
    <div className="cart-drawer">
      <div className="cart-header">
        <h2>üõí Your Cart</h2>
        <button onClick={onClose}>‚ùå</button>
      </div>

      {cartItems.length === 0 ? (
        <p>No items in cart</p>
      ) : (
        <>
          <ul className="cart-items">
            {cartItems.map((item, i) => (
              <li key={i}>
                <span>{item.name}</span>
                <span>‚Çπ{item.price} √ó {item.quantity}</span>
              </li>
            ))}
          </ul>
          <h3 className="total">Total: ‚Çπ{calculateTotal()}</h3>
          <button className="submit-btn" onClick={() => setShowForm(true)}>
            Submit Order
          </button>
        </>
      )}

      {showForm && (
        <div className="form-popup">
          <h3>Enter Details</h3>
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleInputChange}
          />
          <input
            type="tel"
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="location"
            placeholder="Your Location"
            value={formData.location}
            onChange={handleInputChange}
          />
          <button className="submit-btn" onClick={handleFormSubmit}>Send to WhatsApp</button>
        </div>
      )}
    </div>
  );
};

export default CartDrawer;
