import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");

  // Fetch products and categories from backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => {
        // Ensure products have trimmed categories for consistency
        const prods = res.data.map((p) => ({
          ...p,
          category: p.category ? p.category.trim() : "",
        }));
        setProducts(prods);
      })
      .catch((err) => console.error("Error loading products:", err));

    axios
      .get("http://localhost:5000/api/categories")
      .then((res) => {
        // Trim categories here too
        const cats = res.data.map((c) => c.trim());
        setCategories(cats);
      })
      .catch((err) => console.error("Error loading categories:", err));
  }, []);

  // Filter products by selected category, safely handling missing category fields
  const filteredProducts = selectedCategory
    ? products.filter(
        (p) => p.category && p.category.trim() === selectedCategory.trim()
      )
    : products;

  // Debug logs to inspect data
  console.log("Selected category:", selectedCategory);
  console.log("Filtered products:", filteredProducts);

  return (
    <div className="products-container">
      {/* Sidebar with categories */}
      <div className="sidebar">
        <h2>Categories</h2>
        <button
          className="clear-btn"
          onClick={() => setSelectedCategory("")}
          disabled={selectedCategory === ""}
        >
          Show All
        </button>
        <ul>
          {categories.map((cat, index) => (
            <li
              key={index}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </li>
          ))}
        </ul>
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.length === 0 ? (
          <p>No products found.</p>
        ) : (
          filteredProducts.map((p) => (
            <div className="product-card" key={p._id}>
              <img src={p.image} alt={p.name} />
              <h3>{p.name}</h3>
              <p>â‚¹{p.price}</p>
              <p className="product-category">{p.category}</p>
              <button>Add to Cart</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
