// src/components/CartDrawer.jsx
import React, { useState } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';

function CartDrawer({ isOpen, onClose, cartItems, onRemoveFromCart, onUpdateQuantity }) {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '', location: '' });

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFormSubmit = () => {
    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2);
    
    const message = `üõí *Order Details*:
üë§ Name: ${formData.name}
üìû Phone: ${formData.phone}
üìç Location: ${formData.location}

üß® *Items*:
${cartItems.map(item => `‚Ä¢ ${item.name} x${item.quantity} - ‚Çπ${item.price * item.quantity}`).join('\n')}

üí∞ *Total: ‚Çπ${total}*`;

    const encoded = encodeURIComponent(message);

    // ‚ö†Ô∏è Replace with your actual WhatsApp number (include country code, no + or spaces)
    const whatsappNumber = '919876543210'; // Example: India number
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encoded}`;

    // Open WhatsApp Web or App with pre-filled message
    window.open(whatsappURL, '_blank');
  };

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">√ó</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map(item => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>
              Total: ‚Çπ{cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2)}
            </h3>

            {!showForm ? (
              <button className="purchase-button" onClick={() => setShowForm(true)}>
                Submit
              </button>
            ) : (
              <div className="checkout-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                />
                <input
                  type="text"
                  name="location"
                  placeholder="Your Place"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                />
                <button className="submit-final" onClick={handleFormSubmit}>
                  Submit Order
                </button>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
