// Register.jsx
import React, { useState } from "react";

export default function Register() {
  const [mode, setMode] = useState("email"); // "email" or "phone"
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [isOtpSent, setIsOtpSent] = useState(false);

  const handleEmailRegister = (e) => {
    e.preventDefault();
    alert(`‚úÖ Registered with Email: ${email}`);
    alert("üéâ You are now logged in!");
  };

  const handleSendOtp = () => {
    if (!phone) {
      alert("Please enter your phone number");
      return;
    }
    // Simulate OTP sending
    setIsOtpSent(true);
    alert(`üì© OTP sent to ${phone}`);
  };

  const handlePhoneRegister = (e) => {
    e.preventDefault();
    if (otp === "1234") {
      alert(`‚úÖ Registered with Phone: ${phone}`);
      alert("üéâ You are now logged in!");
    } else {
      alert("‚ùå Invalid OTP. Try again.");
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "400px", margin: "auto" }}>
      <h2>Register</h2>

      {/* Mode Switch */}
      <div style={{ marginBottom: "1rem" }}>
        <button
          onClick={() => setMode("email")}
          style={{
            padding: "0.5rem 1rem",
            marginRight: "0.5rem",
            background: mode === "email" ? "#dd2476" : "#ccc",
            color: mode === "email" ? "white" : "black",
            border: "none",
          }}
        >
          Email & Password
        </button>
        <button
          onClick={() => setMode("phone")}
          style={{
            padding: "0.5rem 1rem",
            background: mode === "phone" ? "#dd2476" : "#ccc",
            color: mode === "phone" ? "white" : "black",
            border: "none",
          }}
        >
          Phone & OTP
        </button>
      </div>

      {/* Email Registration */}
      {mode === "email" && (
        <form onSubmit={handleEmailRegister} style={{ display: "flex", flexDirection: "column" }}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ margin: "0.5rem 0", padding: "0.5rem" }}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ margin: "0.5rem 0", padding: "0.5rem" }}
          />
          <button type="submit" style={{ padding: "0.7rem", background: "#dd2476", color: "white", border: "none" }}>
            Register & Login
          </button>
        </form>
      )}

      {/* Phone Registration */}
      {mode === "phone" && (
        <form onSubmit={handlePhoneRegister} style={{ display: "flex", flexDirection: "column" }}>
          <input
            type="tel"
            placeholder="Phone Number"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
            style={{ margin: "0.5rem 0", padding: "0.5rem" }}
          />
          {isOtpSent && (
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
              style={{ margin: "0.5rem 0", padding: "0.5rem" }}
            />
          )}
          {!isOtpSent ? (
            <button
              type="button"
              onClick={handleSendOtp}
              style={{ padding: "0.7rem", background: "#ff9800", color: "white", border: "none" }}
            >
              Send OTP
            </button>
          ) : (
            <button
              type="submit"
              style={{ padding: "0.7rem", background: "#dd2476", color: "white", border: "none" }}
            >
              Register & Login
            </button>
          )}
        </form>
      )}
    </div>
  );
}
