import React, { useEffect, useState, useRef } from "react";
import "./Home.css";

import diwali1 from "./images/diwali1.jpg";
import diwali2 from "./images/diwali2.jpg";

import priceListPdf from "./files/price-list.pdf";
import gpayQr from "./images/gpay-qr.png";

const images = [diwali1, diwali2];

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers";
  const [currentSlide, setCurrentSlide] = useState(0);
  const productRefs = useRef([]);
  const [visibleProducts, setVisibleProducts] = useState(new Set());

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(slideTimer);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const index = Number(entry.target.dataset.index);
            setVisibleProducts((prev) => new Set(prev).add(index));
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.2 }
    );

    productRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="home-container">
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>{greetingText}</span>
          <span>{greetingText}</span>
        </div>
      </div>

      <div className="slideshow full-width">
        <div
          className="slides"
          style={{ transform: `translateX(-${currentSlide * 100}vw)` }}
        >
          {images.map((src, idx) => (
            <div className="slide-wrapper" key={idx}>
              {idx === 0 && <div className="discount-badge">60% OFF</div>}
              {idx === 1 && (
                <div
                  className="order-now-3d"
                  onClick={() => (window.location.href = "/products")}
                >
                  Order Now
                </div>
              )}
              <img src={src} alt={`Slide ${idx + 1}`} className="slide" />
            </div>
          ))}
        </div>
        <div className="dots">
          {images.map((_, idx) => (
            <span
              key={idx}
              className={`dot ${idx === currentSlide ? "active" : ""}`}
              onClick={() => setCurrentSlide(idx)}
            />
          ))}
        </div>
      </div>

      <main className="main-content">
        <section className="products-section">
          <h2>Our Products</h2>
          <div className="products-grid">
            {Array.from({ length: 20 }).map((_, idx) => (
              <div
                key={idx}
                ref={(el) => (productRefs.current[idx] = el)}
                data-index={idx}
                className={`product-card ${
                  visibleProducts.has(idx) ? "fade-in" : "hidden"
                }`}
              >
                <img
                  src={`https://via.placeholder.com/250x150?text=Product+${
                    idx + 1
                  }`}
                  alt={`Product ${idx + 1}`}
                  className="product-image"
                />
              </div>
            ))}
          </div>
          <button
            className="order-now-button"
            onClick={() => (window.location.href = "/products")}
          >
            Order Now â†’
          </button>
        </section>

        <section className="services-section">
          <div className="service-card">
            <h3>24/7 Service</h3>
            <p>We are always available to assist you at any time, day or night.</p>
          </div>
          <div className="service-card">
            <h3>Transport Service</h3>
            <p>Fast and safe delivery of your orders right to your doorstep.</p>
          </div>
        </section>
      </main>

      <footer className="main-footer">
        <div className="footer-content">
          <div className="contact-info">
            <h4>Contact Us</h4>
            <p>Email: support@sripathrakali.com</p>
            <p>Phone: +91 81443 06667</p>
            <p>+91 73051 71319</p>
            <p>Address: 123 Crackers St, Firework City, India</p>
            <p>
              <a
                href={priceListPdf}
                target="_blank"
                rel="noopener noreferrer"
                className="pdf-link"
              >
                ðŸ“„ Download Price List (PDF)
              </a>
            </p>
          </div>
          <div className="bank-details">
            <h4>Bank Details</h4>
            <p>Bank: Crackers National Bank</p>
            <p>Account No: 1234567890</p>
            <p>IFSC Code: CRBK0001234</p>
            <div className="gpay-qr-container">
              <img src={gpayQr} alt="GPay QR Code" className="gpay-qr" />
              <p>Scan to Pay via GPay</p>
            </div>
          </div>
        </div>
        <p className="footer-copy">
          &copy; 2025 Sri Pathrakali Crackers. All rights reserved.
        </p>
      </footer>
    </div>
  );
}
