import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const normalize = (str) =>
    typeof str === "string" ? str.trim().toLowerCase() : "uncategorized";
  const isSameCategory = (a, b) => normalize(a) === normalize(b);
  const capitalize = (str) =>
    str && str.length > 0
      ? str.charAt(0).toUpperCase() + str.slice(1).toLowerCase()
      : "";

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((res) => {
        const prods = res.data.map((p) => ({ ...p, category: normalize(p.category) }));
        setProducts(prods);
      })
      .catch((err) => console.error("❌ Error loading products:", err));

    axios.get("http://localhost:5000/api/categories")
      .then((res) => {
        const cats = res.data.map((c) => normalize(c));
        setCategories(cats);
      })
      .catch((err) => console.error("❌ Error loading categories:", err));
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter((p) => isSameCategory(p.category, selectedCategory))
    : products;

  return (
    <div className="products-container">
      <div className="sidebar">
        <div className="sidebar-header"><strong>Product Categories</strong></div>
        <ul>
          {categories.map((cat, index) => (
            <li key={index}
              className={isSameCategory(selectedCategory, cat) ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}>
              {capitalize(cat)} <span className="count">(
                {products.filter((p) => isSameCategory(p.category, cat)).length})
              </span>
            </li>
          ))}
        </ul>
        <button className="clear-btn"
          onClick={() => setSelectedCategory(null)}
          disabled={selectedCategory === null}>
          Show All
        </button>
      </div>

      <div className="products-section">
        <div className="products-header">
          <h2>{selectedCategory ? capitalize(selectedCategory) : "All Products"}</h2>
          <span>Showing {filteredProducts.length} results</span>
        </div>

        <div className="product-grid">
          {filteredProducts.map((p, index) => (
            <div className="product-card" key={p._id || index}>
              <div className="image-wrapper">
                <img
                  src={p.image}
                  alt={p.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://via.placeholder.com/200x150?text=No+Image";
                  }}
                />
                <div className="offer-badge">80% OFF</div>
              </div>
              <p className="category-label">{capitalize(p.category)}</p>
              <h3>{p.name}</h3>
              <p className="price">
                <span className="old-price">₹{(p.price * 5).toFixed(2)}</span> ₹{p.price}
              </p>
              <button className="enquiry-btn">Add to Enquiry</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
