import React, { useState, useEffect } from 'react';
import './Products.css';
useEffect(() => {
  axios.get('http://localhost:5000/api/products')
    .then(res => setProducts(res.data))
    .catch(err => console.error(err));
}, []);
const Products = () => {
  const productTypes = ['All', 'Crackers', 'Sparklers', 'Rockets'];
  const [selectedType, setSelectedType] = useState('All');
  const [cart, setCart] = useState({});

  const products = [
    { id: 1, name: 'Cracker A', type: 'Crackers', image: '/images/cracker1.jpg' },
    { id: 2, name: 'Sparkler B', type: 'Sparklers', image: '/images/sparkler1.jpg' },
    { id: 3, name: 'Rocket C', type: 'Rockets', image: '/images/rocket1.jpg' },
    { id: 4, name: 'Cracker D', type: 'Crackers', image: '/images/cracker2.jpg' },
    // Add more if needed
  ];

  const filtered = selectedType === 'All'
    ? products
    : products.filter((p) => p.type === selectedType);

  const handleQuantity = (id, delta) => {
    setCart((prev) => {
      const qty = (prev[id] || 0) + delta;
      return { ...prev, [id]: Math.max(qty, 0) };
    });
  };

  const handleAddToCart = (id) => {
    setCart((prev) => ({ ...prev, [id]: (prev[id] || 1) }));
    alert('Added to cart!');
  };

  return (
    <div className="product-page">
      <aside className="sidebar">
        {productTypes.map((type) => (
          <button
            key={type}
            className={selectedType === type ? 'active' : ''}
            onClick={() => setSelectedType(type)}
          >
            {type}
          </button>
        ))}
      </aside>

      <main className="product-grid">
        {filtered.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h4>{product.name}</h4>
            <div className="quantity">
              <button onClick={() => handleQuantity(product.id, -1)}>-</button>
              <span>{cart[product.id] || 0}</span>
              <button onClick={() => handleQuantity(product.id, 1)}>+</button>
            </div>
            <button className="add-btn" onClick={() => handleAddToCart(product.id)}>
              Add to Cart
            </button>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Products;
