import React, { useEffect, useState, useRef } from "react";
import "./Home.css";

import diwali1 from "./images/diwali1.jpg";
// You can replace or add more images
const images = [diwali1, diwali1, diwali1];

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers";
  const [currentSlide, setCurrentSlide] = useState(0);

  // Track visible products on scroll for animation
  const productRefs = useRef([]);

  // Track which products are visible (animated)
  const [visibleProducts, setVisibleProducts] = useState([]);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500);
    return () => clearInterval(slideTimer);
  }, []);

  // Scroll event handler to trigger animation when product cards come into view
  useEffect(() => {
    const onScroll = () => {
      const visibleIndices = [];
      productRefs.current.forEach((el, idx) => {
        if (!el) return;
        const rect = el.getBoundingClientRect();
        // Trigger when top of card is less than 75% viewport height
        if (rect.top < window.innerHeight * 0.75) {
          visibleIndices.push(idx);
        }
      });
      setVisibleProducts(visibleIndices);
    };

    onScroll(); // initial check
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <div className="home-container">
      {/* 🔔 Animated Greeting Only */}
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>{greetingText}</span>
          <span>{greetingText}</span>
        </div>
      </div>

      {/* 🔁 Slideshow */}
      <main className="main-content">
        <div className="slideshow">
          <div
            className="slides"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {images.map((src, idx) => (
              <img
                key={idx}
                src={src}
                alt={`Slide ${idx + 1}`}
                className="slide"
              />
            ))}
          </div>
          <div className="dots">
            {images.map((_, idx) => (
              <span
                key={idx}
                className={`dot ${idx === currentSlide ? "active" : ""}`}
                onClick={() => setCurrentSlide(idx)}
                aria-label={`Go to slide ${idx + 1}`}
              />
            ))}
          </div>
        </div>

        {/* 🎇 Products Section */}
        <section className="products-section">
          <h2>Featured Products</h2>
          <div className="products-grid">
            {Array.from({ length: 20 }).map((_, idx) => (
              <div
                key={idx}
                ref={(el) => (productRefs.current[idx] = el)}
                className={`product-card ${
                  visibleProducts.includes(idx)
                    ? idx % 2 === 0
                      ? "slide-in-left"
                      : "slide-in-right"
                    : "hidden"
                }`}
                style={{ animationDelay: `${idx * 0.1}s` }}
              >
                <img
                  src={`https://via.placeholder.com/200x140?text=Product+${idx + 1}`}
                  alt={`Product ${idx + 1}`}
                  className="product-image"
                />
              </div>
            ))}
          </div>
          <button
            className="order-now-button"
            onClick={() => (window.location.href = "/products")}
          >
            Order Now →
          </button>
        </section>
      </main>

      <footer className="main-footer">
        <p>&copy; 2025 Sri Pathrakali Crackers. All rights reserved.</p>
      </footer>
    </div>
  );
}
