// src/components/CartItem.jsx
import React from 'react';
import '../pages/Cart.css';

function CartItem({ item, onRemoveFromCart, onUpdateQuantity }) {
  const handleDecrease = () => {
    if (item.quantity > 1) onUpdateQuantity(item._id, item.quantity - 1);
  };

  const handleIncrease = () => {
    if (item.quantity < 100) onUpdateQuantity(item._id, item.quantity + 1);
  };

  return (
    <div className="cart-item">
      <img
        src={`http://localhost:5000${item.image}`}
        alt={item.name}
        className="cart-item-image"
      />

      <div className="cart-item-details">
        <h3>{item.name}</h3>
        <p>Price: ₹{item.price.toFixed(2)}</p>

        <div className="quantity-controls">
          <button onClick={handleDecrease} disabled={item.quantity <= 1}>
            -
          </button>
          <span>{item.quantity}</span>
          <button onClick={handleIncrease} disabled={item.quantity >= 100}>
            +
          </button>
        </div>

        <p>Subtotal: ₹{(item.price * item.quantity).toFixed(2)}</p>
        <button className="remove-button" onClick={() => onRemoveFromCart(item._id)}>
          Remove
        </button>
      </div>
    </div>
  );
}

export default CartItem;
