import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null); // null = show all

  // Fetch products and categories
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => {
        const prods = res.data.map((p) => ({
          ...p,
          category: p.category ? p.category.trim().toLowerCase() : "",
        }));
        setProducts(prods);
      })
      .catch((err) => console.error("Error loading products:", err));

    axios
      .get("http://localhost:5000/api/categories")
      .then((res) => {
        const cats = res.data.map((c) => c.trim().toLowerCase());
        setCategories(cats);
      })
      .catch((err) => console.error("Error loading categories:", err));
  }, []);

  // Optional: Pre-select category for testing
  // useEffect(() => setSelectedCategory("sparklers"), []);

  // Filter products based on selected category
  const filteredProducts = selectedCategory
    ? products.filter((p) =>
        p.category &&
        p.category.toLowerCase().includes(selectedCategory.toLowerCase())
      )
    : products;

  // Capitalize utility
  const capitalize = (str) =>
    str && str.length > 0 ? str.charAt(0).toUpperCase() + str.slice(1) : "";

  return (
    <div className="products-container">
      <div className="sidebar">
        <h2>Categories</h2>
        <button
          className="clear-btn"
          onClick={() => setSelectedCategory(null)}
          disabled={selectedCategory === null}
        >
          Show All
        </button>
        <ul>
          {categories.map((cat, index) => (
            <li
              key={index}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {capitalize(cat)}
            </li>
          ))}
        </ul>
      </div>

      <div className="product-grid">
        {filteredProducts.length === 0 ? (
          <p>No products found.</p>
        ) : (
          filteredProducts.map((p) => (
            <div className="product-card" key={p._id}>
              <img
                src={
                  p.image && p.image.startsWith("http")
                    ? p.image
                    : "https://via.placeholder.com/200x150?text=Image+Coming+Soon"
                }
                alt={p.name}
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "https://via.placeholder.com/200x150?text=Image+Coming+Soon";
                }}
              />
              <h3>{p.name}</h3>
              <p>â‚¹{p.price}</p>
              <p className="product-category">{capitalize(p.category)}</p>
              <button>Add to Cart</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
