import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [cart, setCart] = useState({});

  useEffect(() => {
    // Dummy 21 product categories
    const demoCategories = Array.from({ length: 21 }, (_, i) => `Category ${i + 1}`);
    setCategories(demoCategories);

    // Fetch product data from backend
    axios.get("http://localhost:5000/api/products").then((res) => {
      setProducts(res.data);
    });
  }, []);

  const filteredProducts =
    selectedCategory === null
      ? products
      : selectedCategory === "__none__"
      ? []
      : products.filter((p) => p.category === selectedCategory);

  const toggleAll = () => {
    setSelectedCategory((prev) => (prev === null ? "__none__" : null));
  };

  const addToCart = (product) => {
    setCart((prev) => ({
      ...prev,
      [product._id]: {
        ...product,
        quantity: prev[product._id] ? prev[product._id].quantity + 1 : 1,
      },
    }));
  };

  const removeFromCart = (productId) => {
    setCart((prev) => {
      const updated = { ...prev };
      if (updated[productId]) {
        updated[productId].quantity > 1
          ? updated[productId].quantity--
          : delete updated[productId];
      }
      return updated;
    });
  };

  const total = Object.values(cart).reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="product-page">
      <aside className="sidebar">
        <h2>Product Categories</h2>
        <ul>
          {/* "All" at the top with toggle icon */}
          <li
            className={selectedCategory === null ? "active" : ""}
            onClick={toggleAll}
          >
            All{" "}
            <span style={{ marginLeft: 6 }}>
              {selectedCategory === null ? "▲" : "▼"}
            </span>
          </li>

          {categories.map((cat) => (
            <li
              key={cat}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      <main className="main-content">
        <div className="product-grid">
          {filteredProducts.map((p) => (
            <div key={p._id} className="product-card">
              <img src={p.image} alt={p.name} />
              <h3>{p.name}</h3>
              <p>₹{p.price}</p>
              <div className="quantity-control">
                <button onClick={() => removeFromCart(p._id)}>-</button>
                <span>{cart[p._id]?.quantity || 0}</span>
                <button onClick={() => addToCart(p)}>+</button>
              </div>
              <button className="add-btn" onClick={() => addToCart(p)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>

        <div className="cart-box">
          <h3>Cart</h3>
          {Object.keys(cart).length === 0 ? (
            <p>No items added.</p>
          ) : (
            <>
              <ul>
                {Object.values(cart).map((item) => (
                  <li key={item._id}>
                    {item.name} x {item.quantity} = ₹{item.price * item.quantity}
                  </li>
                ))}
              </ul>
              <h4>Total: ₹{total}</h4>
              <button className="pay-btn">Pay Now</button>
            </>
          )}
        </div>
      </main>
    </div>
  );
}
