// server.js
const express = require("express");
const cors = require("cors");
const app = express();
const PORT = 5000;

app.use(cors()); // Enable CORS for all routes

// âœ… Root route to prevent "Cannot GET /"
app.get("/", (req, res) => {
  res.send("ðŸŽ† Cracker Shop Backend is Running!");
});

// Categories
const categories = [
  "One Sound Crackers", "Sparklers", "Flower Pots", "Chakras", "Roman Candles",
  "Fountains", "Smoke Bombs", "Ground Spinners", "Rocket Missiles", "Sound Crackers",
  "Glow Sticks", "Fireworks Kits", "Party Poppers", "Flames", "Firecrackers",
  "Celebration Packs", "Kids Fireworks", "Diwali Specials", "Indoor Fireworks",
  "Outdoor Fireworks", "Festival Collection"
];

// Products
const products = Array.from({ length: 100 }, (_, i) => ({
  _id: `prod${i + 1}`,
  name: `Product ${i + 1}`,
  category: categories[i % categories.length],
  price: Math.floor(Math.random() * 500 + 100), // 100 to 600
  image: `https:/via.placeholder.com/200x150?text=Product+${i + 1}/`
}));

// âœ… Route: Get all products
app.get("/api/products", (req, res) => {
  res.json(products);
});

// âœ… Route: Get all categories
app.get("/api/categories", (req, res) => {
  res.json(categories);
});
app.get("/api/categories", (req, res) => {
  const uniqueCategories = [...new Set(products.map((p) => p.category))];
  res.json(uniqueCategories);
});
// âœ… Start server
app.listen(PORT, () => {
  console.log(`âœ… Server running at: http://localhost:${PORT}`);
});
