import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [selectedType, setSelectedType] = useState('All');

  // Step 1: Initialize cart from localStorage or empty object
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : {};
  });

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(res => {
        const fixedProducts = res.data.map((product, index) => ({
          ...product,
          _id: product._id || `fallback-${index}`, // fallback id
          image: product.image?.startsWith('http')
            ? product.image
            : `http://localhost:5000/images/cracker${(index % 10) + 1}.jpg`, // fallback image
          type: (product.category || product.type || 'Unknown').toLowerCase(),
        }));
        setProducts(fixedProducts);
      })
      .catch(err => console.error('Failed to fetch products:', err));
  }, []);

  // Step 2: Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const getProductTypes = () => {
    const types = [...new Set(products.map(p => p.type))];
    return ['All', ...types.map(t => t.charAt(0).toUpperCase() + t.slice(1))];
  };

  const filtered = selectedType === 'All'
    ? products
    : products.filter(p => p.type.toLowerCase() === selectedType.toLowerCase());

  const handleQuantity = (id, delta) => {
    setCart(prev => {
      const qty = (prev[id] || 0) + delta;
      if (qty <= 0) {
        const newCart = { ...prev };
        delete newCart[id];
        return newCart;
      }
      return { ...prev, [id]: qty };
    });
  };

  const handleAddToCart = (id) => {
    setCart(prev => ({ ...prev, [id]: (prev[id] || 0) + 1 }));
  };

  const totalPrice = Object.entries(cart).reduce((total, [id, qty]) => {
    const product = products.find(p => p._id === id);
    return total + (product ? product.price * qty : 0);
  }, 0);

  return (
    <div className="product-page">
      <aside className="sidebar">
        {getProductTypes().map(type => (
          <button
            key={type}
            className={selectedType.toLowerCase() === type.toLowerCase() ? 'active' : ''}
            onClick={() => setSelectedType(type)}
          >
            {type}
          </button>
        ))}
      </aside>

      <main className="product-grid">
        {filtered.length === 0 ? (
          <p style={{ textAlign: 'center', width: '100%' }}>No products available.</p>
        ) : (
          filtered.map((product, index) => (
            <div key={product._id || index} className="product-card">
              <img src={product.image} alt={product.name} />
              <h4>{product.name}</h4>
              <p>â‚¹{product.price}</p>
              <div className="quantity">
                <button onClick={() => handleQuantity(product._id, -1)}>-</button>
                <span>{cart[product._id] || 0}</span>
                <button onClick={() => handleQuantity(product._id, 1)}>+</button>
              </div>
              <button className="add-btn" onClick={() => handleAddToCart(product._id)}>
                Add to Cart
              </button>
            </div>
          ))
        )}
      </main>

      {/* Cart Summary Panel */}
      <div className="cart-card">
        <h3>ðŸ›’ Cart</h3>
        {Object.keys(cart).length === 0 ? (
          <p>No items in cart.</p>
        ) : (
          <ul>
            {Object.entries(cart).map(([id, quantity]) => {
              const product = products.find(p => p._id === id);
              if (!product) return null;
              return (
                <li key={id}>
                  {product.name} Ã— {quantity} = â‚¹{product.price * quantity}
                </li>
              );
            })}
          </ul>
        )}
        <h4>Total: â‚¹{totalPrice}</h4>
      </div>
    </div>
  );
};

export default Products;
