// src/App.js
import React, { useReducer } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar'; // Your existing Navbar
import Home from './pages/Home'; // Your existing Home page
import About from './pages/About'; // Your existing About page
import Login from './pages/Login'; // Your existing Login page
import Products from './pages/Products'; // Your existing Products page
import Cart from './pages/Cart'; // New Cart page
import './App.css'; // Your existing App styles

// Cart Reducer Logic
const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_ITEM':
      const existingItem = state.find(item => item.id === action.payload.id);
      if (existingItem) {
        return state.map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      return [...state, { ...action.payload, quantity: 1 }];

    case 'REMOVE_ITEM':
      return state.filter(item => item.id !== action.payload.id);

    case 'UPDATE_QUANTITY':
      const { id, quantity } = action.payload;
      if (quantity < 1) {
        return state.filter(item => item.id !== id); // Remove if quantity goes to 0
      }
      return state.map(item =>
        item.id === id
          ? { ...item, quantity }
          : item
      );

    default:
      return state;
  }
};

function App() {
  const [cartItems, dispatch] = useReducer(cartReducer, []);

  const handleAddToCart = (product) => {
    dispatch({ type: 'ADD_ITEM', payload: product });
  };

  const handleRemoveFromCart = (id) => {
    dispatch({ type: 'REMOVE_ITEM', payload: { id } });
  };

  const handleUpdateQuantity = (id, newQuantity) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { id, quantity: newQuantity } });
  };

  const getTotalCartItems = () => {
      return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <Router>
      <Navbar cartItemCount={getTotalCartItems()} /> {/* Pass cart item count to Navbar */}
      <div className="main-content"> {/* Optional: for main content styling */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/products"
            element={<Products onAddToCart={handleAddToCart} />}
          />
          <Route
            path="/cart"
            element={
              <Cart
                cartItems={cartItems}
                onRemoveFromCart={handleRemoveFromCart}
                onUpdateQuantity={handleUpdateQuantity}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
