import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null); // null means show all

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => {
        console.log("Raw products:", res.data);
        const prods = res.data.map((p) => ({
          ...p,
          category: p.category ? p.category.trim().toLowerCase() : "",
        }));
        console.log("Processed products:", prods);
        setProducts(prods);
      })
      .catch((err) => console.error("Error loading products:", err));

    axios
      .get("http://localhost:5000/api/categories")
      .then((res) => {
        console.log("Raw categories:", res.data);
        const cats = res.data.map((c) => c.trim().toLowerCase());
        console.log("Processed categories:", cats);
        setCategories(cats);
      })
      .catch((err) => console.error("Error loading categories:", err));
  }, []);

  const filteredProducts = selectedCategory
    ? products.filter((p) => {
        if (!p.category) return false;
        return p.category.trim().toLowerCase() === selectedCategory.trim().toLowerCase();
      })
    : products;

  const capitalize = (str) =>
    str.charAt(0).toUpperCase() + str.slice(1);

  return (
    <div className="products-container">
      <div className="sidebar">
        <h2>Categories</h2>
        <button
          className="clear-btn"
          onClick={() => setSelectedCategory(null)}
          disabled={selectedCategory === null}
        >
          Show All
        </button>
        <ul>
          {categories.map((cat, index) => (
            <li
              key={index}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {capitalize(cat)}
            </li>
          ))}
        </ul>
      </div>

      <div className="product-grid">
        {filteredProducts.length === 0 ? (
          <p>No products found.</p>
        ) : (
          filteredProducts.map((p) => (
            <div className="product-card" key={p._id}>
              <img src={p.image} alt={p.name} />
              <h3>{p.name}</h3>
              <p>â‚¹{p.price}</p>
              <p className="product-category">{capitalize(p.category)}</p>
              <button>Add to Cart</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
