// src/components/Navbar.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { ShoppingCart } from "lucide-react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick }) {
  const user = JSON.parse(localStorage.getItem("user"));
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  const handleSuccess = (credentialResponse) => {
    try {
      const userData = jwtDecode(credentialResponse.credential);

      const cleanUser = {
        name: userData.name,
        email: userData.email,
        picture: userData.picture || null,
      };

      localStorage.setItem("user", JSON.stringify(cleanUser));
      window.location.reload();
    } catch (err) {
      console.error("JWT Decode Error:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    window.location.reload();
  };

  const getInitial = (user) => {
    if (user?.name) return user.name.charAt(0).toUpperCase();
    if (user?.email) return user.email.charAt(0).toUpperCase();
    return "?";
  };

  // ✅ Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <nav className="navbar">
      {/* Logo */}
      <Link to="/" className="logo">
        Sri Pathrakali Crackers
      </Link>

      {/* Navigation Links */}
      <div className="nav-links">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/after-payment">After Payment</Link>

        {/* Cart */}
        <button className="cart-button" onClick={onCartClick}>
          <ShoppingCart size={22} />
          <span style={{ marginLeft: "4px" }}>{cartItemCount}</span>
        </button>

        {/* Auth Section */}
        <div className="auth-section" ref={dropdownRef}>
          {user ? (
            <div style={{ position: "relative" }}>
              {/* Profile Icon (Picture OR Initial) */}
              <div
                onClick={() => setDropdownOpen(!dropdownOpen)}
                style={{
                  width: "36px",
                  height: "36px",
                  borderRadius: "50%",
                  background: "#007bff",
                  color: "white",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  cursor: "pointer",
                  overflow: "hidden",
                  fontWeight: "bold",
                  fontSize: "16px",
                  textTransform: "uppercase",
                }}
              >
                {user.picture ? (
                  <img
                    src={user.picture}
                    alt="profile"
                    style={{ width: "100%", height: "100%", borderRadius: "50%" }}
                  />
                ) : (
                  getInitial(user)
                )}
              </div>

              {/* Dropdown Menu */}
              {dropdownOpen && (
                <div
                  style={{
                    position: "absolute",
                    top: "45px",
                    right: "0",
                    background: "#fff",
                    border: "1px solid #ddd",
                    borderRadius: "8px",
                    boxShadow: "0px 4px 6px rgba(0,0,0,0.1)",
                    padding: "10px",
                    minWidth: "150px",
                    zIndex: 1000,
                  }}
                >
                  <p
                    style={{
                      margin: "0 0 8px 0",
                      fontWeight: "bold",
                      fontSize: "14px",
                      color: "#333",
                    }}
                  >
                    {user.name || user.email}
                  </p>
                  <button
                    onClick={handleLogout}
                    style={{
                      background: "red",
                      color: "white",
                      border: "none",
                      padding: "6px 12px",
                      borderRadius: "6px",
                      cursor: "pointer",
                      width: "100%",
                      fontSize: "14px",
                    }}
                  >
                    Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <GoogleLogin
              onSuccess={handleSuccess}
              onError={() => alert("❌ Login Failed")}
              useOneTap
            />
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
