import React, { useEffect, useState } from "react";
import "./Home.css"; // Ensure this CSS file exists and is linked correctly

// Import your images. Make sure these files exist in the './images/' directory
// and that their filenames (including case and extension) match exactly.
import diwali1 from "./images/diwali1.jpg";
import diwali2 from "./images/diwali1.jpg"; // Assuming you have this image
import diwali3 from "./images/diwali1.jpg"; // Assuming you have this image

export default function Home() {
  const greetingText = "Welcome to CrackerMart";
  const [visibleLetters, setVisibleLetters] = useState(0);

  // Use all your imported images for the slideshow
  const images = [diwali1, diwali, diwali3];
  const [currentSlide, setCurrentSlide] = useState(0);

  // Animate greeting letters sliding in one by one (from left to right)
  useEffect(() => {
    if (visibleLetters < greetingText.length) {
      const timer = setTimeout(() => {
        setVisibleLetters(visibleLetters + 1);
      }, 150); // Adjust delay for speed of animation
      return () => clearTimeout(timer);
    }
  }, [visibleLetters, greetingText.length]);

  // Slideshow auto change every 3 seconds
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 3000); // Change slide every 3 seconds
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      <div className="greeting">
        {/* Render each letter with a span for individual animation */}
        {greetingText.split("").map((char, idx) => (
          <span
            key={idx}
            className={`letter ${idx < visibleLetters ? "visible" : ""}`}
            style={{ transitionDelay: `${idx * 0.08}s` }} // Faster individual letter delay
          >
            {char === " " ? "\u00A0" : char} {/* Render space as non-breaking space */}
          </span>
        ))}
      </div>

      <div className="slideshow">
        <div
          className="slides"
          // This transform moves the entire 'slides' container horizontally
          // based on the current slide index, creating the sliding effect.
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {images.map((src, idx) => (
            <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
          ))}
        </div>
        <div className="dots">
          {images.map((_, idx) => (
            <span
              key={idx}
              className={`dot ${idx === currentSlide ? "active" : ""}`}
              onClick={() => setCurrentSlide(idx)}
              aria-label={`Go to slide ${idx + 1}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
