import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null); // null = all
  const [cart, setCart] = useState({});
  const [showCategories, setShowCategories] = useState(true); // ðŸ‘ˆ New toggle state

  useEffect(() => {
    const demoCategories = Array.from({ length: 21 }, (_, i) => `Category ${i + 1}`);
    setCategories(demoCategories);

    axios.get("http://localhost:5000/api/products").then((res) => {
      setProducts(res.data);
    });
  }, []);

  const toggleCategoryList = () => {
    setShowCategories((prev) => !prev);
  };

  const filteredProducts =
    selectedCategory
      ? products.filter((p) => p.category === selectedCategory)
      : products;

  const addToCart = (product) => {
    setCart((prev) => ({
      ...prev,
      [product._id]: {
        ...product,
        quantity: prev[product._id] ? prev[product._id].quantity + 1 : 1,
      },
    }));
  };

  const removeFromCart = (productId) => {
    setCart((prev) => {
      const updated = { ...prev };
      if (updated[productId]) {
        updated[productId].quantity > 1
          ? updated[productId].quantity--
          : delete updated[productId];
      }
      return updated;
    });
  };

  const total = Object.values(cart).reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="product-page">
      <aside className="sidebar">
        <h2>Product Categories</h2>
        <ul>
          <li onClick={toggleCategoryList} className="toggle-btn">
            All {showCategories ? "â–²" : "â–¼"}
          </li>

          {showCategories &&
            categories.map((cat) => (
              <li
                key={cat}
                className={selectedCategory === cat ? "active" : ""}
                onClick={() => setSelectedCategory(cat)}
              >
                {cat}
              </li>
            ))}
        </ul>
      </aside>

      <main className="main-content">
        <div className="product-grid">
          {filteredProducts.map((p) => (
            <div key={p._id} className="product-card">
              <img src={p.image} alt={p.name} />
              <h3>{p.name}</h3>
              <p>â‚¹{p.price}</p>
              <div className="quantity-control">
                <button onClick={() => removeFromCart(p._id)}>-</button>
                <span>{cart[p._id]?.quantity || 0}</span>
                <button onClick={() => addToCart(p)}>+</button>
              </div>
              <button className="add-btn" onClick={() => addToCart(p)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>

        <div className="cart-box">
          <h3>Cart</h3>
          {Object.keys(cart).length === 0 ? (
            <p>No items added.</p>
          ) : (
            <>
              <ul>
                {Object.values(cart).map((item) => (
                  <li key={item._id}>
                    {item.name} x {item.quantity} = â‚¹{item.price * item.quantity}
                  </li>
                ))}
              </ul>
              <h4>Total: â‚¹{total}</h4>
              <button className="pay-btn">Pay Now</button>
            </>
          )}
        </div>
      </main>
    </div>
  );
}
