// src/pages/AfterPayment.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './AfterPayment.css';

function AfterPayment() {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [place, setPlace] = useState('');
  const [landmark, setLandmark] = useState('');
  const [pdfFile, setPdfFile] = useState(null);
  const [screenshot, setScreenshot] = useState(null);

  const handleSubmit = () => {
    if (!name || !phone || !place || !landmark) {
      alert("Please fill in all the details.");
      return;
    }

    const message = `üßæ *Order Confirmation*\n\nüë§ Name: ${name}\nüìû Phone: ${phone}\nüìç Place: ${place}\nüìå Landmark: ${landmark}\n\nPlease find the uploaded bill and payment screenshot.`;

    // Encode the message
    const encodedMessage = encodeURIComponent(message);

    // Replace this number with your WhatsApp number (with country code, no +)
    const whatsappNumber = "91XXXXXXXXXX";

    // Open WhatsApp with message
    window.open(`https://wa.me/${whatsappNumber}?text=${encodedMessage}`, "_blank");
  };

  return (
    <div className="after-payment-page">
      <div className="success-card">
        <h2>‚úÖ Payment Successful!</h2>
        <p>Thank you for your purchase at <strong>CrackerMart</strong>.</p>

        <div className="order-details">
          <p><strong>üßæ Order ID:</strong> #CM{Date.now()}</p>
          <p><strong>üìÖ Date:</strong> {new Date().toLocaleDateString('en-IN')}</p>
          <p><strong>üìç Location:</strong> Tamil Nadu, India</p>
        </div>

        <div className="customer-form">
          <input
            type="text"
            placeholder="üë§ Your Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="tel"
            placeholder="üìû Phone Number"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <input
            type="text"
            placeholder="üìç Place"
            value={place}
            onChange={(e) => setPlace(e.target.value)}
          />
          <input
            type="text"
            placeholder="üìå Landmark (Near Lorry Set Place)"
            value={landmark}
            onChange={(e) => setLandmark(e.target.value)}
          />

          <label>üßæ Upload PDF Bill:</label>
          <input type="file" accept="application/pdf" onChange={(e) => setPdfFile(e.target.files[0])} />

          <label>üí∞ Upload Payment Screenshot:</label>
          <input type="file" accept="image/*" onChange={(e) => setScreenshot(e.target.files[0])} />

          <button className="submit-button" onClick={handleSubmit}>
            üì© Submit to WhatsApp
          </button>
        </div>

        <Link to="/products" className="back-button">
          üõç Continue Shopping
        </Link>
      </div>
    </div>
  );
}

export default AfterPayment;
