// src/components/Navbar.jsx
import React from "react";
import { Link } from "react-router-dom";
import { ShoppingCart, UserCircle, LogOut } from "lucide-react"; // ✅ Better icons
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick, isLoggedIn }) {
  return (
    <nav className="navbar">
      {/* Logo */}
      <Link to="/" className="logo">
        Sri Pathrakali Crackers
      </Link>

      {/* Navigation Links */}
      <div className="nav-links">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/after-payment">After Payment</Link>

        {/* Cart */}
        <button className="cart-button" onClick={onCartClick}>
          <ShoppingCart size={22} />{" "}
          <span style={{ marginLeft: "4px" }}>{cartItemCount}</span>
        </button>

        {/* User Auth Section (YouTube-like) */}
        <div className="auth-section">
          {isLoggedIn ? (
            // ✅ If logged in → show profile + logout
            <Link to="/logout" className="auth-link">
              <UserCircle size={26} style={{ marginRight: "6px" }} />
              Logout
            </Link>
          ) : (
            // ❌ If not logged in → show "Sign in"
            <Link to="/login" className="auth-link">
              <UserCircle size={26} style={{ marginRight: "6px" }} />
              Sign in
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
