// src/components/CartDrawer.jsx
import React from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';

function CartDrawer({ isOpen, onClose, cartItems, onRemoveFromCart, onUpdateQuantity, onPurchase }) {
  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">×</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map(item => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>
              Total: ₹{cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2)}
            </h3>
            <button className="purchase-button" onClick={onPurchase}>
              Purchase with GPay
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
