import React, { useEffect, useState } from "react";
import "./Home.css";

import diwali1 from "./images/diwali1.jpg";
// import diwali2 from "./images/diwali2.jpg";
// import diwali3 from "./images/diwali3.jpg";

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers";
  const images = [diwali1, diwali1, diwali1]; // Replace with unique images later

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500);
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      {/* ðŸ”” Animated Greeting Only */}
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>{greetingText}</span>
          <span>{greetingText}</span>
        </div>
      </div>

      {/* Main Content */}
      <main className="main-content">
        <div className="slideshow">
          <div
            className="slides"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {images.map((src, idx) => (
              <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
            ))}
          </div>
          <div className="dots">
            {images.map((_, idx) => (
              <span
                key={idx}
                className={`dot ${idx === currentSlide ? "active" : ""}`}
                onClick={() => setCurrentSlide(idx)}
                aria-label={`Go to slide ${idx + 1}`}
              />
            ))}
          </div>
        </div>

        <section className="about-section">
          <h2>About Our Crackers</h2>
          <p>Discover a wide range of high-quality and safe crackers for all your celebrations. We prioritize safety and customer satisfaction.</p>
        </section>
      </main>
      <section className="products-section">
  <h2>Our Products</h2>
  <div className="products-grid">
    {[...Array(20)].map((_, idx) => (
      <div className="product-card" key={idx}>
        <img
          src={`./images/product${(idx % 5) + 1}.jpg`} // Add 5 sample product images and reuse
          alt={`Product ${idx + 1}`}
          className="product-image"
        />
        <button
          className="order-button"
          onClick={() => window.location.href = "/products"}
        >
          Order Now
        </button>
      </div>
    ))}
  </div>
</section>


      <footer className="main-footer">
        <p>&copy; 2025 Sri Pathrakali Crackers. All rights reserved.</p>
      </footer>
    </div>
  );
}
