import React, { useEffect, useState } from "react";
import "./Home.css";
import diwali1 from "./images/diwali1.jpg"; // import your images
import diwali2 from "./images/diwali2.jpg";
import diwali3 from "./images/diwali3.jpg";

export default function Home() {
  const greetingText = "Welcome to CrackerMart";
  const [visibleLetters, setVisibleLetters] = useState(0);

  const images = [diwali1, diwali2, diwali3]; // your images
  const [currentSlide, setCurrentSlide] = useState(0);

  // Animate greeting letters sliding in one by one
  useEffect(() => {
    if (visibleLetters < greetingText.length) {
      const timer = setTimeout(() => {
        setVisibleLetters(visibleLetters + 1);
      }, 150);
      return () => clearTimeout(timer);
    }
  }, [visibleLetters, greetingText.length]);

  // Slideshow auto change every 3 seconds
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      <div className="greeting">
        {/* Slide each visible letter with span */}
        {greetingText.split("").map((char, idx) => (
          <span
            key={idx}
            className={`letter ${idx < visibleLetters ? "visible" : ""}`}
            style={{ transitionDelay: `${idx * 0.15}s` }}
          >
            {char === " " ? "\u00A0" : char}
          </span>
        ))}
      </div>

      <div className="slideshow">
        <div
          className="slides"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {images.map((src, idx) => (
            <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
          ))}
        </div>
        <div className="dots">
          {images.map((_, idx) => (
            <span
              key={idx}
              className={`dot ${idx === currentSlide ? "active" : ""}`}
              onClick={() => setCurrentSlide(idx)}
              aria-label={`Go to slide ${idx + 1}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
