  import React, { useState } from 'react';
  import CartItem from './CartItem';
  import './CartDrawer.css';
  import jsPDF from 'jspdf';
  import html2canvas from 'html2canvas';

  function CartDrawer({
    isOpen,
    onClose,
    cartItems,
    onRemoveFromCart,
    onUpdateQuantity,
    onClearCart // <- NEW PROP
  }) {
    const [showForm, setShowForm] = useState(false);
    const [formData, setFormData] = useState({
      name: '',
      phone: '',
      location: ''
    });

    const handleInputChange = (e) => {
      setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleFirstSubmit = () => {
      const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
      if (total < 3000) {
        alert('âš ï¸ Purchase minimum â‚¹3000 to proceed.');
        return;
      }
      if (cartItems.length === 0) {
        alert('âš ï¸ Your cart is empty.');
        return;
      }
      setShowForm(true);
    };

    const handleFormSubmit = () => {
      const { name, phone, location } = formData;

      if (!name.trim() || !phone.trim() || !location.trim()) {
        alert('âš ï¸ Please fill in all the details before submitting.');
        return;
      }

      const now = new Date();
      const date = now.toLocaleDateString('en-IN');
      const time = now.toLocaleTimeString('en-IN');

      const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

      const itemsMessage = cartItems.map((item, index) =>
        `${index + 1}. ${item.name}\n   Qty: ${item.quantity} Ã— â‚¹${item.price} = â‚¹${item.price * item.quantity}`
      ).join('\n\n');

      const message = `ðŸ§¨ *CrackerMart Order Bill* ðŸ§¨

  ðŸ•’ *Order Time:* ${date} - ${time}

  ðŸ‘¤ *Customer Info*
  â€¢ Name: ${name}
  â€¢ Phone: ${phone}
  â€¢ Location: ${location}

  ðŸ“¦ *Items Ordered*
  ${itemsMessage}

  ðŸ’° *Total Bill*: â‚¹${total.toFixed(2)}

  ðŸ“Œ Thank you for your order!
  âœ… Kindly confirm and provide delivery details.
  `;

      const encodedMessage = encodeURIComponent(message);
      const whatsappNumber = '8148842731';
      const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encodedMessage}`;

      window.open(whatsappURL, '_blank');

      // âœ… Clear cart after sending
      if (onClearCart) onClearCart();

      // âœ… Close the cart drawer and reset form
      onClose();
      setShowForm(false);
      setFormData({ name: '', phone: '', location: '' });
    };

    const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0);

    return (
      <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
        <div className="cart-drawer-header">
          <h2>Your Cart</h2>
          <button onClick={onClose} className="close-button">Ã—</button>
        </div>

        {cartItems.length === 0 ? (
          <p className="empty-cart">Your cart is empty</p>
        ) : (
          <>
            <div className="cart-items">
              {cartItems.map((item) => (
                <CartItem
                  key={item._id}
                  item={item}
                  onRemoveFromCart={onRemoveFromCart}
                  onUpdateQuantity={onUpdateQuantity}
                  disabled={false}
                />
              ))}
            </div>

            <div className="cart-drawer-summary">
              <h3>Total: â‚¹{total.toFixed(2)}</h3>

              {!showForm && (
                <button className="purchase-button" onClick={handleFirstSubmit}>
                  Submit
                </button>
              )}

              {showForm && (
                <div className="checkout-form">
                  <input
                    type="text"
                    name="name"
                    placeholder="Your Name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="tel"
                    name="phone"
                    placeholder="Phone Number"
                    value={formData.phone}
                    onChange={handleInputChange}
                    required
                  />
                  <input
                    type="text"
                    name="location"
                    placeholder="Your Place"
                    value={formData.location}
                    onChange={handleInputChange}
                    required
                  />

                  <button className="submit-final" onClick={handleFormSubmit}>
                    ðŸ“¤ Send Bill & Order to WhatsApp
                  </button>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    );
  }

  export default CartDrawer;
