import React, { useEffect, useState } from "react";
import "./Home.css"; // Ensure this CSS file exists and is linked correctly

// Import your images. Make sure these files exist in the './images/' directory
// and that their filenames (including case and extension) match exactly.
// IMPORTANT: For a slideshow, you typically want DIFFERENT images here.
import diwali1 from "./images/diwali1.jpg";
import diwali2 from "./images/diwali1.jpg"; // CHANGE THIS TO diwali2.jpg if you have it!
import diwali3 from "./images/diwali.jpg"; // CHANGE THIS TO diwali3.jpg if you have it!

export default function Home() {
  const greetingText = "Welcome to CrackerMart";

  // Use all your imported images for the slideshow
  // If diwali2.jpg and diwali3.jpg are different images, use them here.
  const images = [diwali1, diwali2, diwali3]; // Make sure these are different images!
  const [currentSlide, setCurrentSlide] = useState(0);

  // Slideshow auto change every 2.5 seconds (as set in previous update)
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500); // Change slide every 2.5 seconds
    return () => clearInterval(slideTimer);
  }, [images.length]);

  return (
    <div className="home-container">
      {/* New structure for the continuous marquee greeting */}
      <div className="greeting-container">
        <span className="greeting-text">{greetingText}</span>
      </div>

      <div className="slideshow">
        <div
          className="slides"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {images.map((src, idx) => (
            <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
          ))}
        </div>
        <div className="dots">
          {images.map((_, idx) => (
            <span
              key={idx}
              className={`dot ${idx === currentSlide ? "active" : ""}`}
              onClick={() => setCurrentSlide(idx)}
              aria-label={`Go to slide ${idx + 1}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
}
