// src/components/CartDrawer.js
import React, { useRef } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

function CartDrawer({ isOpen, onClose, cartItems, onRemoveFromCart, onUpdateQuantity }) {
  const billRef = useRef(null);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleDownloadBill = async () => {
    const billElement = billRef.current;
    if (!billElement) return;

    try {
      const canvas = await html2canvas(billElement);
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgProps = pdf.getImageProperties(imgData);
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save('CrackerMart_Bill.pdf');
    } catch (error) {
      console.error('Failed to generate PDF:', error);
    }
  };

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">&times;</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>Total: â‚¹{total.toFixed(2)}</h3>
            <button className="purchase-button" onClick={handleDownloadBill}>
              ðŸ“„ Download Bill
            </button>
          </div>

          <div
            ref={billRef}
            className="bill-preview"
            style={{
              padding: '20px',
              width: '210mm',
              background: 'white',
              color: 'black',
              fontSize: '14px',
              lineHeight: '1.6',
              display: 'none' // Hidden from screen but rendered for PDF
            }}
          >
            <h2>ðŸ§³ CrackerMart Bill</h2>
            <p><strong>Date:</strong> {new Date().toLocaleDateString('en-IN')}</p>
            <hr />
            <ul>
              {cartItems.map((item, i) => (
                <li key={i}>
                  {i + 1}. {item.name} â€” Qty: {item.quantity} Ã— â‚¹{item.price} = â‚¹{item.quantity * item.price}
                </li>
              ))}
            </ul>
            <hr />
            <h3>Total: â‚¹{total.toFixed(2)}</h3>
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
