import React, { useState } from 'react';
import './Products.css';
const Product = () => {
  const productTypes = ['All', 'Crackers', 'Sparklers', 'Rockets'];

  const products = [
    {
      id: 1,
      name: 'Sky Rocket',
      type: 'Rockets',
      image: '/images/cracker1.jpg',
    },
    {
      id: 2,
      name: 'Twinkling Sparklers',
      type: 'Sparklers',
      image: '/images/cracker2.jpg',
    },
    {
      id: 3,
      name: 'Thunder Bomb',
      type: 'Crackers',
      image: '/images/cracker3.jpg',
    },
    {
      id: 4,
      name: 'Mega Rocket',
      type: 'Rockets',
      image: '/images/cracker.jpg',
    },
  ];

  const [selectedType, setSelectedType] = useState('All');
  const [cart, setCart] = useState([]);
  const [quantities, setQuantities] = useState({});

  const handleQuantityChange = (id, change) => {
    setQuantities(prev => ({
      ...prev,
      [id]: Math.max((prev[id] || 1) + change, 1),
    }));
  };

  const handleAddToCart = (product) => {
    const qty = quantities[product.id] || 1;
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item =>
        item.id === product.id ? { ...item, qty: item.qty + qty } : item
      ));
    } else {
      setCart([...cart, { ...product, qty }]);
    }
  };

  const filteredProducts =
    selectedType === 'All'
      ? products
      : products.filter(p => p.type === selectedType);

  return (
    <div className="product-page">
      <div className="sidebar">
        <h3>Types</h3>
        {productTypes.map(type => (
          <button
            key={type}
            className={`type-button ${
              type === selectedType ? 'active' : ''
            }`}
            onClick={() => setSelectedType(type)}
          >
            {type}
          </button>
        ))}
      </div>

      <div className="main">
        <h2>{selectedType} Products</h2>
        <div className="product-list">
          {filteredProducts.map(product => (
            <div className="product-card" key={product.id}>
              <img src={product.image} alt={product.name} />
              <h4>{product.name}</h4>
              <div className="quantity">
                <button onClick={() => handleQuantityChange(product.id, -1)}>-</button>
                <span>{quantities[product.id] || 1}</span>
                <button onClick={() => handleQuantityChange(product.id, 1)}>+</button>
              </div>
              <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          ))}
        </div>
      </div>

      <div className="cart">
        <h3>Bill</h3>
        {cart.length === 0 ? (
          <p>No items added.</p>
        ) : (
          cart.map(item => (
            <div key={item.id}>
              {item.name} Ã— {item.qty}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Product;
