import React, { useEffect, useState, useRef } from "react";
import "./Home.css";

import diwali1 from "./images/diwali1.jpg";
import diwali2 from "./images/diwali2.jpg";

const images = [diwali1, diwali2];

export default function Home() {
  const greetingText = "Welcome to Sri Pathrakali Crackers";
  const [currentSlide, setCurrentSlide] = useState(0);

  const productRefs = useRef([]);
  const [visibleProducts, setVisibleProducts] = useState(new Set());

  // Slideshow
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % images.length);
    }, 2500);
    return () => clearInterval(slideTimer);
  }, []);

  // Scroll detection - one-by-one reveal
  useEffect(() => {
    const onScroll = () => {
      setVisibleProducts((prevSet) => {
        const newSet = new Set(prevSet);
        for (let idx = 0; idx < productRefs.current.length; idx++) {
          const el = productRefs.current[idx];
          if (el && !newSet.has(idx)) {
            const rect = el.getBoundingClientRect();
            if (rect.top < window.innerHeight * 0.85) {
              newSet.add(idx); // Add only one product per scroll
              break;
            }
          }
        }
        return newSet;
      });
    };

    onScroll(); // Initial check
    const interval = setInterval(onScroll, 150); // smooth effect
    window.addEventListener("scroll", onScroll);

    return () => {
      clearInterval(interval);
      window.removeEventListener("scroll", onScroll);
    };
  }, []);

  return (
    <div className="home-container">
      {/* 🔔 Animated Greeting */}
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>{greetingText}</span>
          <span>{greetingText}</span>
        </div>
      </div>

      {/* 🔁 Slideshow */}
      <main className="main-content">
        <div className="slideshow">
          <div
            className="slides"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {images.map((src, idx) => (
              <img key={idx} src={src} alt={`Slide ${idx + 1}`} className="slide" />
            ))}
          </div>
          <div className="dots">
            {images.map((_, idx) => (
              <span
                key={idx}
                className={`dot ${idx === currentSlide ? "active" : ""}`}
                onClick={() => setCurrentSlide(idx)}
                aria-label={`Go to slide ${idx + 1}`}
              />
            ))}
          </div>
        </div>

        {/* 🎇 Products Section */}
        <section className="products-section">
          <h2>Our Products</h2>
          <div className="products-grid">
            {Array.from({ length: 20 }).map((_, idx) => (
              <div
                key={idx}
                ref={(el) => (productRefs.current[idx] = el)}
                className={`product-card ${
                  visibleProducts.has(idx) ? "fade-in" : "hidden"
                }`}
                style={{ animationDelay: `${(idx % 5) * 0.1}s` }}
              >
                <img
                  src={`https://via.placeholder.com/200x140?text=Product+${idx + 1}`}
                  alt={`Product ${idx + 1}`}
                  className="product-image"
                />
              </div>
            ))}
          </div>
          <button
            className="order-now-button"
            onClick={() => (window.location.href = "/products")}
          >
            Order Now →
          </button>
        </section>
      </main>

      <footer className="main-footer">
        <p>&copy; 2025 Sri Pathrakali Crackers. All rights reserved.</p>
      </footer>
    </div>
  );
}
