// cartdrawer.jsx
import React, { useState } from 'react';
import CartItem from './CartItem';
import './CartDrawer.css'; // Assuming this CSS file contains the styling for .submit-final

function CartDrawer({
  isOpen,
  onClose,
  cartItems,
  onRemoveFromCart,
  onUpdateQuantity
}) {
  const [showForm, setShowForm] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    location: ''
  });

  const handleInputChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFirstSubmit = () => {
    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    if (total < 3000) {
      // Replaced alert with a custom message box for better UX
      // In a real application, you'd render a modal or toast here
      // For this example, I'll use a console log to avoid blocking the iframe
      console.log('âš ï¸ Purchase minimum â‚¹3000 to proceed.');
      // You would implement a custom modal/snackbar component here
      // For now, let's proceed for demo purposes if not within a real app sandbox
      return;
    }
    setShowForm(true);
  };

  const handleFormSubmit = () => {
    // Basic form validation
    if (!formData.name || !formData.phone || !formData.location) {
      console.log('Please fill in all details.'); // Use a proper UI message instead of alert
      return;
    }

    const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

    const message = `ðŸ›’ *Order Details*:
ðŸ‘¤ Name: ${formData.name}
ðŸ“ž Phone: ${formData.phone}
ðŸ“ Location: ${formData.location}

ðŸ§¨ *Items*:
${cartItems
  .map((item) => `â€¢ ${item.name} x${item.quantity} - â‚¹${item.price * item.quantity}`)
  .join('\n')}

ðŸ’° *Total: â‚¹${total.toFixed(2)}*`;

    const encoded = encodeURIComponent(message);
    const whatsappNumber = '8148842731'; // Replace with your actual WhatsApp number
    const whatsappURL = `https://wa.me/${whatsappNumber}?text=${encoded}`;

    window.open(whatsappURL, '_blank');

    // ðŸ”’ Lock cart after successful submission
    setSubmitted(true);
  };

  const handleAddMore = () => {
    // ðŸ”“ Reopen cart for modifications
    setSubmitted(false);
    setShowForm(false);
  };

  const isDisabled = submitted;
  const total = cartItems.reduce((sum, i) => sum + i.price * i.quantity, 0);

  return (
    <div className={`cart-drawer ${isOpen ? 'open' : ''}`}>
      <div className="cart-drawer-header">
        <h2>Your Cart</h2>
        <button onClick={onClose} className="close-button">Ã—</button>
      </div>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onRemoveFromCart={onRemoveFromCart}
                onUpdateQuantity={onUpdateQuantity}
                disabled={isDisabled} // ðŸš« disables editing/removal if submitted
              />
            ))}
          </div>

          <div className="cart-drawer-summary">
            <h3>Total: â‚¹{total.toFixed(2)}</h3>

            {/* âœ… Show initial 'Submit' button only if form is not shown and not submitted */}
            {!showForm && !submitted && (
              <button className="purchase-button" onClick={handleFirstSubmit}>
                Submit
              </button>
            )}

            {/* âœ… Show form and final submission button when triggered and not yet submitted */}
            {showForm && !submitted && (
              <div className="checkout-form">
                <input
                  type="text"
                  name="name"
                  placeholder="Your Name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  disabled={submitted}
                />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Phone Number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  required
                  disabled={submitted}
                />
                <input
                  type="text"
                  name="location"
                  placeholder="Your Place"
                  value={formData.location}
                  onChange={handleInputChange}
                  required
                  disabled={submitted}
                />

                <button className="submit-final" onClick={handleFormSubmit}>
                  Submit the Details with Bill
                </button>
              </div>
            )}

            {/* âœ… Show "Add More Items" button only after submission, using the same style class */}
            {submitted && (
              <button className="submit-final add-more-button" onClick={handleAddMore}>
                Add More Items
              </button>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CartDrawer;
