import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Products.css";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null); // null means all shown
  const [cart, setCart] = useState({});

  useEffect(() => {
    // Dummy categories
    const demoCategories = Array.from({ length: 21 }, (_, i) => `Category ${i + 1}`);
    setCategories(demoCategories);

    axios.get("http://localhost:5000/api/products").then((res) => {
      setProducts(res.data);
    });
  }, []);

  // Toggle all category on/off
  const toggleAllCategory = () => {
    setSelectedCategory((prev) => (prev === null ? "NONE" : null));
  };

  // Filter products based on selected category
  const filteredProducts =
    selectedCategory === "NONE"
      ? [] // hide all products
      : selectedCategory
      ? products.filter((p) => p.category === selectedCategory)
      : products; // null means show all

  // Add/remove from cart functions remain the same
  // ...

  return (
    <div className="product-page">
      <aside className="sidebar">
        <h2>Product Categories</h2>
        <ul>
          <li
            className={selectedCategory === null ? "active" : ""}
            onClick={toggleAllCategory}
          >
            All {selectedCategory === null ? "▲" : "▼"}
          </li>
          {categories.map((cat) => (
            <li
              key={cat}
              className={selectedCategory === cat ? "active" : ""}
              onClick={() => setSelectedCategory(cat)}
            >
              {cat}
            </li>
          ))}
        </ul>
      </aside>

      <main className="main-content">
        <div className="product-grid">
          {filteredProducts.map((p) => (
            <div key={p._id} className="product-card">
              <img src={p.image} alt={p.name} />
              <h3>{p.name}</h3>
              <p>₹{p.price}</p>
              <div className="quantity-control">
                <button onClick={() => removeFromCart(p._id)}>-</button>
                <span>{cart[p._id]?.quantity || 0}</span>
                <button onClick={() => addToCart(p)}>+</button>
              </div>
              <button className="add-btn" onClick={() => addToCart(p)}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>

        {/* Cart component code unchanged */}
      </main>
    </div>
  );
}
