import React, { useEffect, useState } from "react";
import "./Home.css";

const slides = [
  { id: 1, image: "/images/diwali.jpg", discount: "60% OFF" },
  
  { id: 3, image: "/images/diwali.jpg", discount: "FESTIVE DEAL" },
];

const products = [
  { id: 1, name: "Flower Pots", image: "/images/flower-pots.jpg" },
  { id: 2, name: "Sparklers", image: "/images/sparklers.jpg" },
  { id: 3, name: "Ground Chakkar", image: "/images/ground-chakkar.jpg" },
  { id: 4, name: "Rockets", image: "/images/rockets.jpg" },
  { id: 5, name: "Garland Crackers", image: "/images/garland.jpg" },
  { id: 6, name: "Color Matches", image: "/images/color-matches.jpg" },
  { id: 7, name: "Pencil Crackers", image: "/images/pencil.jpg" },
  { id: 8, name: "Atom Bombs", image: "/images/atom.jpg" },
  { id: 9, name: "Sky Shots", image: "/images/skyshots.jpg" },
  { id: 10, name: "Twinkling Stars", image: "/images/twinkling.jpg" },
];

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("visible");
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.15 });

    const cards = document.querySelectorAll(".product-card");
    cards.forEach((card) => observer.observe(card));

    return () => observer.disconnect();
  }, []);

  return (
    <div className="home-container">
      <div className="greeting-wrapper">
        <div className="greeting-text-inner">
          <span>Welcome to CrackersMart!</span>
          <span>Festival Offers Await!</span>
          <span>Shop Safely, Celebrate Brightly!</span>
        </div>
      </div>

      <div className="slideshow full-width">
        <div
          className="slides"
          style={{ transform: `translateX(-${currentSlide * 100}vw)` }}
        >
          {slides.map((slide, index) => (
            <div className="slide-wrapper" key={slide.id}>
              <img src={slide.image} alt={`Slide ${index}`} className="slide" />
              <div className="discount-badge">{slide.discount}</div>
              <div className="order-now-3d">Order Now</div>
            </div>
          ))}
        </div>
        <div className="dots">
          {slides.map((_, idx) => (
            <div
              key={idx}
              className={`dot ${idx === currentSlide ? "active" : ""}`}
              onClick={() => setCurrentSlide(idx)}
            ></div>
          ))}
        </div>
      </div>

      <div className="main-content">
        <div className="products-section">
          <h2>Our Crackers</h2>
          <div className="products-grid">
            {products.map((prod) => (
              <div className="product-card fade-in-on-scroll" key={prod.id}>
                <img
                  src={prod.image}
                  alt={prod.name}
                  className="product-image"
                />
                <h3>{prod.name}</h3>
              </div>
            ))}
          </div>
        </div>

        <div className="services-section">
          <div className="service-card">
            <h3>Fast Delivery</h3>
            <p>Get your orders delivered on time at your doorstep.</p>
          </div>
          <div className="service-card">
            <h3>Bulk Orders</h3>
            <p>Special discounts and packages for large quantity purchases.</p>
          </div>
          <div className="service-card">
            <h3>Safe Packing</h3>
            <p>Carefully packed items with safety precautions.</p>
          </div>
        </div>
      </div>

      <footer className="main-footer">
        <div className="footer-content">
          <div className="contact-info">
            <h4>Contact</h4>
            <p>Phone: +91-9876543210</p>
            <p>Email: support@crackersmart.com</p>
          </div>
          <div className="bank-details">
            <h4>Bank Details</h4>
            <p>Name: CrackersMart Pvt Ltd</p>
            <p>IFSC: ICIC0001234</p>
            <p>Account: 1234567890</p>
          </div>
          <div className="gpay-qr-container">
            <img
              src="/images/gpay-qr.png"
              alt="GPay QR"
              className="gpay-qr"
            />
            <p>Scan & Pay</p>
          </div>
        </div>
        <p className="footer-copy">&copy; 2025 CrackersMart. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
