import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ProductItem from '../components/ProductItem';
import './Products.css';

function Products({ onAddToCart }) {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('All');
  const navigate = useNavigate();

  const categories = ['All', 'Sparklers', 'Crackers', 'Rockets'];

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(res => res.json())
      .then(data => {
        const formatted = data.map(p => ({
          ...p,
          imageUrl: `http://localhost:5000${p.image}`,
        }));
        setProducts(formatted);
        setFiltered(formatted);
      })
      .catch(err => {
        console.error("Failed to fetch products:", err);
      });
  }, []);

  // Filter products on search or category change
  useEffect(() => {
    let temp = [...products];

    if (category !== 'All') {
      temp = temp.filter(p => p.category === category);
    }

    if (search.trim()) {
      const keyword = search.toLowerCase();
      temp = temp.filter(p => p.name.toLowerCase().includes(keyword));
    }

    setFiltered(temp);
  }, [search, category, products]);

  // Group products by category for headings
  const groupedProducts = filtered.reduce((acc, product) => {
    const cat = product.category || 'Others';
    if (!acc[cat]) acc[cat] = [];
    acc[cat].push(product);
    return acc;
  }, {});

  return (
    <div className="products-page">
      <h2>üéÜ Our Fireworks Products</h2>

      <div className="filters" style={{ marginBottom: '1rem', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="üîç Search products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />

        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          {categories.map((cat, i) => (
            <option key={i} value={cat}>{cat}</option>
          ))}
        </select>

        <button onClick={() => { setSearch(''); setCategory('All'); }}>Reset</button>
      </div>

      <div className="products-grid">
        {filtered.length === 0 ? (
          <div className="not-found">‚ùå No products found</div>
        ) : (
          Object.entries(groupedProducts).map(([catName, items]) => (
            <div key={catName}>
              <h3 style={{ margin: '20px 0 10px', color: '#c0392b' }}>{catName}</h3>
              <div className="product-category-group">
                {items.map(product => (
                  <ProductItem
                    key={product._id}
                    product={product}
                    onAddToCart={() => onAddToCart(product, navigate)}
                    showDiscount={true}
                  />
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Products;
