// App.jsx
import React, { useReducer } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import About from './pages/About';
import Login from './pages/Login';
import Products from './pages/Products';
import Cart from './pages/Cart';
import './App.css';

// Example cartReducer — you need to fill in your actual reducer logic here
const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_ITEM': {
      const product = action.payload;
      const existingItem = state.cartItems.find(item => item.id === product.id);
      if (existingItem) {
        // Increase quantity if already in cart
        return {
          ...state,
          cartItems: state.cartItems.map(item =>
            item.id === product.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          ),
        };
      } else {
        // Add new item
        return {
          ...state,
          cartItems: [...state.cartItems, { ...product, quantity: 1 }],
        };
      }
    }

    case 'REMOVE_ITEM': {
      const idToRemove = action.payload.id;
      return {
        ...state,
        cartItems: state.cartItems.filter(item => item.id !== idToRemove),
      };
    }

    case 'UPDATE_QUANTITY': {
      const { id, quantity } = action.payload;
      if (quantity <= 0) {
        // Remove item if quantity zero or less
        return {
          ...state,
          cartItems: state.cartItems.filter(item => item.id !== id),
        };
      }
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item.id === id ? { ...item, quantity } : item
        ),
      };
    }

    default:
      return state;
  }
};

function App() {
  const initialState = { cartItems: [] };
  const [state, dispatch] = useReducer(cartReducer, initialState);

  const handleAddToCart = (product, navigate) => {
    dispatch({ type: 'ADD_ITEM', payload: product });
    if (navigate) navigate('/cart');
  };

  const handleRemoveFromCart = (id) => {
    dispatch({ type: 'REMOVE_ITEM', payload: { id } });
  };

  const handleUpdateQuantity = (id, newQuantity) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { id, quantity: newQuantity } });
  };

  const getTotalCartItems = () => {
    return state?.cartItems?.reduce((total, item) => total + item.quantity, 0) || 0;
  };

  return (
    <Router>
      <Navbar cartItemCount={getTotalCartItems()} />

      <div className="main-content">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/products"
            element={
              <Products
                onAddToCart={(product, navigate) => handleAddToCart(product, navigate)}
              />
            }
          />
          <Route
            path="/cart"
            element={
              <Cart
                cartItems={state.cartItems}
                onRemoveFromCart={handleRemoveFromCart}
                onUpdateQuantity={handleUpdateQuantity}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
