// src/components/Navbar.jsx
import React from "react";
import { Link } from "react-router-dom";
import { LogIn, LogOut } from "lucide-react"; // âœ… Import icons
import "./Navbar.css";

function Navbar({ cartItemCount, onCartClick, isLoggedIn }) {
  return (
    <nav className="navbar">
      <Link to="/" className="logo">
        Sri Pathrakali Crackers
      </Link>

      <div className="nav-links">
        <Link to="/products">Products</Link>
        <Link to="/about">About</Link>
        <Link to="/after-payment">After Payment</Link>

        {/* Cart icon with count */}
        <button className="cart-button" onClick={onCartClick}>
          ðŸ›’ {cartItemCount}
        </button>

        {/* Login / Logout Icon with tick/cross */}
        <div className="icon-wrapper" title={isLoggedIn ? "Logout" : "Please login"}>
          {isLoggedIn ? (
            <Link to="/logout" className="icon-link">
              <LogOut size={24} />
              <span className="status-badge success">âœ”</span>
            </Link>
          ) : (
            <Link to="/login" className="icon-link">
              <LogIn size={24} />
              <span className="status-badge error">âœ–</span>
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
